css:
@-moz-document domain("feedly.com") {
BODY {  
background-image: url(https://s6.postimg.cc/x7p5p5ozl/touhoulineup.jpg) !important;
background-size: 100% 100% !important;
background-position: center !important;
background-color: black !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
color: lightblue !important;
}

.selectedEntry, .entryHolder, .u4Entry, element.style, #feedlyFrame, #content .hentry, .commentlist .children li.comment, a.comment-reply-link, .websiteCallForAction, .u5EntryAnnotationHolder, .u5Entry.textOnly1 .title, .u5Entry.textOnly2 .title, .div, #feedlypart0, #mainbar > #floatingbar, #bigMarkAllAsReadButton, .popup .option, .pro .pro-features, .landing .backgrounded, #searchBarFX   {
background: transparent !important;
color: lightblue !important;
}
  
.column_1_3 {
padding-right: 10px !important;
}  

.area, .subscriptionContentsHolder, .u5entry .title, .u5entry .metadata, .u0Entry, .fx .col-md-7, .fx .today .stream, .price-table-column, .fx .col-md-6, .fx .modal-overlay.is-inline.is-fading, .Leftnav__dock, .fx.fx .LeftnavDock__button {
border-color: transparent !important;
background-color: rgba(0,0,0,.7)!important;
border-radius: 3px !important;
}

.u5Entry, .u14Entry { border: none !important; }
    
.wide .dark #feedlyTabsHolder, #floatingBar, #site-title, #feedlyDialogFramePart, #content .hentry, .u5Entry, .u4Entry, #feedlypart0, .wide .dark #feedlyTabsHolder #feedlyTabs .target.selected, #searchPromo, .floatingEntryScroller, .promo .info, .fx .feed .item-content, .fx .modal-overlay.is-inline.is-fading, .fx .modal-overlay.is-inline.is-fading, .LeftnavList, .LeftnavListRow {
background: rgba(0,0,0,.7)!important; 
}

#feedlyTabs {
background: rgba(0,0,0,.7) !important;
border-radius: 4px !important;
}
    

#page #branding #s {
background: whitesmoke !important;
color: lightblue !Important;
}

.commentlist > li.comment, #page #branding #site-description, p, .u19Entry, .fx .chip-list, .fx .entry.u5{
background-color: rgba(0,0,0,.8)!important;
color: whitesmoke !important;
}

.inlineFrame, .magOverview, .placeholder, #mainBarFX {
background-color: rgba(0,0,0,.8)!important;
color: whitesmoke !important;
border-color: transparent !important;
}

a, .summary, .portfolio .lastPrice, #floatingBar h1, #sideArea .feedIndexTitle, .preferencesItemDescription, .preferencesPanel label, .empty, #page #branding #s, .formContainer .secondaryActionLink, .nonEmpty, h2, .landing .feature .description, .entryBody h4, .entryBody td  {
color: lightblue !important;
}

#sideArea a, h1, .preferencesItemLabel, .fieldLabel, div.simpleUnreadCount:hover, .entriesLoadingMessage, .h2Link, .fx h4, h4.fx, .fx-h4, .fx h4 .h4-label, .datasheet h2, h3, h4, .fx .menu .item {
color: gold !important;
font-size: 20px !important;
text-shadow: black 0 2px 3px!important;
}

.entryBody .summary b, .entryBody .content b, #herculeInput, .article-header .article-headline, .LeftnavListRow__text--bold {
color: silver !important;
}

.pageAction {
opacity: 1 !important;
}

html.slabserif body#box.home.intercom-off div#feedlyPersona.dark div#feedlyChrome.leftnav-open div#feedlyFrame div#feedlyPageHolderFX.fx div#feedlyPageFX.container.centered div.board.presentation-5 div div.empty-state.is-medium img { visibility: hidden !important; }
    

h1 .hAction.secondary, h1 .hAction.tertiary, .pageActionBar, .ab, .fx .feed .item-description {
background:rgba(0,0,0,.2) !important;
color: white !important;
}   

.fx .button-markasread, .fx .button.secondary, .fx button.secondary, .fx-button.secondary, .modal-notifications, .fx .header .actions-and-details-container, .floatingEntryContent .headerInfo, .fx .tabs > .item.active, .fx .board-tag:hover .tabs > .item.delete-tag, .fx .tabs > .item, .fx .following-feed, .fx.popover, .fx .popover, .fx .organize--toolbar.sticky, .LeftnavListRow:hover  {
background-color: #3aa620!important;
background-image: -webkit-linear-gradient(#3aa620,#000) !important;
background-image: -moz-linear-gradient(#3aa620,#000) !important;
background-image: -ms-linear-gradient(#3aa620,#000) !important;
background-image: -o-linear-gradient(#3aa620,#000) !important;
background-image: linear-gradient(#3aa620,#000) !important;
color: white !important;
}
  
.fx .mark-as-read-button span {
color:white !important;
}

#floatingBar .pageAction, .landing .hero, .promo, #feedlyPageHolderFX, #headerBarFX header {
background: transparent !important;
}
    
#feedlyDialogFramePart, .popup, .narrow .picturePicture .panels, .welcomeNav, .subscriptionContentsHolder.dndOver, .newCategoryFactory.dndOver, .tab .feedIndex:hover, #goog-gt-tt, .loginButton, #herculePanel, #herculeHint, .proShowcase, .u0Entry:hover, #fixedProfile, .popup .option:hover, .headerInfo.headerInfo-article, .headerInfo, .u100FeedHeader,  .u100Header-banner, #herculePanel .herculeResult:hover, .relatedLabel, .autocomplete, .autocomplete-feed:hover, #explain .button.secondary, .libraryAnnouncement .message, .libraryModal.libraryAnnouncement .btn, .proB .edition, .bg-dark, .bg-graphite, #headerBarFX, .fixed-bar, .fx .modal, .modal-notifications, .fx.popover, .fx .popover, .fx .MenuItem:hover, .fx .menu--positioned {
background-color: #210e2d !important;
background-image: -webkit-linear-gradient(#210e2d,#000) !important;
background-image: -moz-linear-gradient(#210e2d,#000) !important;
background-image: -ms-linear-gradient(#210e2d,#000) !important;
background-image: -o-linear-gradient(#210e2d,#000) !important;
background-image: linear-gradient(#210e2d,#000) !important;
color: white !important;
border: transparent !important;
}

 .pageActionBar, #herculePanel .separator, .action, .fx .header .button-icon-only, .fx .button-dropdown {
background-color: silver !important;
background-image: -webkit-linear-gradient(silver,#000) !important;
background-image: -moz-linear-gradient(silver,#000) !important;
background-image: -ms-linear-gradient(silver,#000) !important;
background-image: -o-linear-gradient(silver,#000) !important;
background-image: linear-gradient(silver,#000) !important;
border: transparent !important;
}
  
.viewerIcon, .formContainer .actions, .u5Entry.textOnly5 .title, .u5Entry.textOnly4 .title, .u5Entry.textOnly3 .title, .u5Entry.textOnly0 .title, .frameactionstop, #feedlyTabsHolder, .u100FeedHeader .gap, .u14Entry .vignette, .entryholder, #herculeFrame, .fx .feed-header, .landing-old .hero  {
background-color: transparent !important;
border: transparent !important;
}

html.slabserif body#box.home.notSlided.intercom-off div#feedlyPersona.dark div#feedlyChrome.leftnav-open div#feedlyFrame div#feedlyPageHolderFX.fx div#feedlyPageFX.container.centered div.board.presentation-5 div div.empty-state.is-medium img { visibility: hidden !important; }

#herculePanel .herculeResult {
border-top: transparent !important;
}
  
#herculeInput { 
margin-left: 5px !important;
}

.links .group {
border-bottom: transparent !important;
}

.nbrRecommendations, .floatingEntryContent, .dark #feedlyTabsHolder, #feedlyTabsHolder, .fx .empty-state h2, .fx .empty-state h3  {
background: rgba(0,0,0,.8) !important;
}
            
.wide .dark #feedlyTabsHolder, .cell2 h3, .pro h1, .pro h2, .pro h3, .criteria, #helpSearchModule_part > div, .proB .editionInfo, .landing .section .subtitle, .proB .dollarCents, .fx h1 .sub, .fx .MarkAsReadButton .button__label {
color: white !important;
}
    
.entryBody, BODY.home, .proB .priceTag {
color: whitesmoke !important;
}

*.action, .staticValue, .checkboxLabel, .entryBody h3, b, .autocomplete-bold, .fx .item-header, .fx-item-header, .fx .MenuItem {
color: #3aa620 !important;
}

.highlightableIcon, #feedlyTabsHolder div.simpleunreadcount, .slideBumper div {
opacity: 0.8 !important;
}

#allDone, #logoPlaceholderFX, .fx .empty-state.is-medium img, .fx .empty-state {
background: transparent url(https://s6.postimg.cc/vgwhctbr1/Yukkuri_Reimu.png) !important;
background-size: 100% 100% !important;
color: #3aa620 !important;
}
}
