css:
/* 
   Global striped grey background 
   userstyles.org/styles/88088/
*/
@-moz-document
 url-prefix(http:)
,url-prefix(https:)
,url-prefix(file:)
,url-prefix(ftp:)
{
/* * * * /
,url-prefix(chm:file:)
,url-prefix(about:)
/* * * */
html, body{ min-height: 100% !important; }
body {
background-image: none !important;
background-color: #ddd !important;
background-image: repeating-linear-gradient(
0deg , hsla(0, 0%, 89%, 1) .0em, hsla(0, 0%, 85%, 1) 1em, hsla(0, 0%, 91%, 1) 2em) !important; 
}
#under,.under,
.main.serp.i-bem,.serp-header > .serp-header__wrapper /* 160616 yandex */
{ background: transparent  !important; }
}
@-moz-document
 url-prefix("http://www.google.")
,url-prefix("https://www.google.") 
{
[id^="hdtb"],[class^="hdtb"],
#gbx1,#appbar,.clickable-dropdown-arrow,.sfbgg
,.ksb,.ab_button 
,.gb_qb,.fbar
,.gb_ib /*140123*/
{
background-image: repeating-linear-gradient(
0deg , hsla(0, 0%, 92%, 1) .0em, hsla(0, 0%, 86%, 1) 1em, hsla(0, 0%, 93%, 1) 2em) !important; 
}
}
