css:
@-moz-document domain("scratch.mit.edu") {
*{background-color:transparent}
a{color:#E00 !important}
a:hover{color:#A00 !important}
#pagewrapper{background-color:#99F}
#footer{background-color:#006}
#footer p{color:#CC0;font-weight:bold}
.box-content,.box-content *{background-color:#CCF !important}
input, textarea{border-color:#FFF !important}
#footer select{background-color:#CCC;color:#FFF}
a{text-decoration:none !important}
.title{font-weight:bold !important}
#topnav{background-color:#33C}
.closed-topic a,.closed-topic a:hover{color:#888 !important}
.pagination,.linkst li{color:#CCC}
.popover-content{background-color:#22C !important}
blockquote{border-radius:3px;border:1px solid #20D !important}
.code{border-radius:3px;border:1px solid #BBC !important}
hr{border-top:1px #EEE solid !important;width:100% !important}
.button *{border:none;background-color:transparent !important}
.search input{background-color:#EEE}
#sidebar li{background-color:#0F0 !important}
#sidebar li.active{background-color:#0D0 !important}
div.gsc-tabHeader,div.gsc-tabHeader span{background-color:#0A0 !important;color:#045 !important}
div.gsc-tabHeader.gsc-tabhActive{background-color:#080 !important;border:solid 1px #FA0}
}
