css:
@-moz-document domain("irccloud.com") {
* {
    border-radius: 0!important;
    -webkit-border-radius: 0 !important;
}

#sidebar #limits .price, #sidebar #limits #hardZombieWarning, #sidebar #limits .payUpdateWarning {
    display: none !important;
}

div#timeContainer {
    display: none;
}

.twitterwrap iframe {
    border: 0px !important;
}

.standalone-tweet {
    background: rgba(90, 90, 90, 0.43);
}

.standalone-tweet blockquote {
    color: #808080 !important;
}

div.paste h1 {
    border-bottom: 1px solid #808080;
    background-color: #292A24;
}

#sidebar #limits .limitedTrial {
    background-color: #272822;
}

body {
    color: #696969;
    background-color: #272822;
}

a {
    color: #c9d1ff;
}

a:hover, a:active {
    color: #00F;
    outline: 0;
}

.stamp {
    color: #2F4CAB;
    background-color: #292a24;
}

#headercell {
    color: #696969;
}

#headercell a,
h1#title a,
#footer a,
#sidebar #limits a {
    color: #4381FF;
}

#headercell a:hover,
h1#title a:hover,
#footer a:hover,
#sidebar #limits a:hover {
    color: #0048FF;
}

#statusActions {
    color: #41423b;
}

#statusActions b,
#statusActions strong {
    color: #696969;
}

#mainwrapper {
    padding-left: 201px;
    padding-right: 0px;
}

h1#title span.beta {
    color: #527DFF;
}

h1#title span.beta .new {
    color: #183f8d;
}

h1#title span.beta a.new:hover {
    color: #c9d1ff;
}

::-webkit-scrollbar {
    width: 10px;
    height: 10px;
}

::-webkit-scrollbar-track-piece {
    background-color: #f5f5f5;
    background-clip: padding-box;
    -webkit-background-clip: padding-box;
    border: 3px solid #272822;
    border-radius: 0px;
    -webkit-border-radius: 0px;
}

::-webkit-scrollbar-thumb {
    background-color: #527DFF;
    background-clip: padding-box;
    -webkit-background-clip: padding-box;
    border: 3px solid #272822;
    border-radius: 0px;
    -webkit-border-radius: 0px;
}

#sidebar::-webkit-scrollbar-track-piece,
#sidebar::-webkit-scrollbar-thumb {
    border-color: #272822;
}

body.disconnected #sidebar::-webkit-scrollbar-track-piece,
body.disconnected #sidebar::-webkit-scrollbar-thumb {
    border-color: #FFEE97;
}

::-webkit-scrollbar-thumb:window-inactive {
    background-color: #bbb;
}

#sidebar::-webkit-scrollbar-thumb:window-inactive {
    background-color: #9CBAFF;
}

div#sidebarwrapper {
    top: 0;
    left: 0;
}

.mainForm,
.mainContent {
    color: #DBDBDB;
}

.mainContentText h2 a {
    color: #000;
    text-decoration: none;
}

.mainOverlayContent {
    border: 1px solid #000;
    line-height: 1.3;
    background-color: #272822;
    color: #DBDBDB;
}

.sidebarIndicatorFailed {
    background-color: #F8DF26;
    border-color: #DBB300;
}

.sidebarIndicatorUnread {
    background-color: #527DFF;
    border-color: #123e92;
}

.sidebarIndicatorBadged {
    background-color: #fd2200;
    border-color: #D20004;
}

#sidebar .info {
    display: none;
    background-color: #527DFF;
    color: #272822;
}

#sidebar .infoBlank {
    background-color: transparent;
    color: #696969;
}

#sidebar .info a {
    color: #272822;
}

#sidebar .info p.form .input {
    border-color: #313EC9;
}

#sidebar .info p.form .input:focus {
    border-color: #272822;
}

#sidebar .info p.button,
#sidebar .info p.more {
    color: #777;
    background-color: #34352d;
}

#sidebar .info p.button a,
#sidebar .info p.more a {
    color: #777;
}

#sidebar #verifyNotice {
    background-color: #5FA800;
}

#sidebar #limits {
    display: none;
    background-color: #34352d;
    color: #777;
}

li.mono,
p.mono {
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
    font-size: 13px;
}

div.shim, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user {
    font-size: 12px;
    line-height: 1.25;
    font-family: "Consolas", "Menlo", "Ubuntu Mono", "Courier", monospace !important;
}

.buffer.bufferLink.author.user {
    display: inline-block;
    min-width: 125px;
    border-right: solid 1px;
}

.log {
    background-color: #272822;
}

div.log div.chat,
div.log div.status,
div.log div.notice,
div.log div.groupedLines,
div.log div.warning,
div.log div.error,
div.log div.table,
div.log div.me {
    color: #e5e5e5;
}

div.log div.chat a,
div.log div.status a,
div.log div.notice a,
div.log div.groupedLines a,
div.log div.warning a,
div.log div.error a,
div.log div.table a,
div.log div.me a {
