    background-color: #262228;
}

ul.memberList li.admin h2 span.memberCount {
    color: #B580FF;
}

ul.memberList li.admin ul {
    border-color: #3d2f51;
    background-color: #262228;
}

div.log div.row span.mode_OP,
.inputInfo span.mode_OP,
.nickcell span.mode_OP,
#memberContextMenu h3 span.mode_OP {
    color: #C07273;
}

ul.actions li a, .bufferContextMenu.active ul.actions li a, .bufferContextMenu ul.actions li a.delete, .bufferContextMenu ul.actions li a.unread, .bufferContextMenu ul.actions li a.images, .bufferContextMenu ul.actions li a.joinpart, .bufferContextMenu ul.actions li a.joinpartcollapse, .bufferContextMenu ul.actions li a.ignores, #channelContextMenu.active.mod ul.actions li a.topic {
    color: #858585;
