}

#memberContextMenu ul.actions li a:hover, .bufferContextMenu.active ul.actions li a:hover, .bufferContextMenu ul.actions li a.delete:hover, .bufferContextMenu ul.actions li a.unread:hover, .bufferContextMenu ul.actions li a.images:hover, .bufferContextMenu ul.actions li a.joinpart:hover, .bufferContextMenu ul.actions li a.joinpartcollapse:hover, .bufferContextMenu ul.actions li a.ignores:hover, #channelContextMenu.active.mod ul.actions li a.topic:hover {
