css:
@-moz-document regexp('https?://[^.]*\.?flickr.com.*') {

/* Kill glorified Yahoo ad-bar */
#eyebrow {
	visibility: hidden !important;
} 

/* Kill glorified Yahoo ad-bar */
body.with-eyebrow {
	background-position: left top !important;
}

/* Kill glorified Yahoo ad-bar */
body.with-eyebrow #global-nav {
	top: 0 !important;
}

/* Kill glorified Yahoo ad-bar */
.with-eyebrow .subnav-refresh {
	margin-top: 0px !important;

}

}


@-moz-document regexp('https?://[^.]*\.?flickr.com/photos/[^/]+/[0-9]+/?.*') {

/*[[version]]*/

/* Kill glorified Yahoo ad-bar */
#eyebrow {
	visibility: hidden !important;
}

.comments-view .comments-form .comment-form-field textarea {
	color: #000;
	background: #fff;
	border: 1px solid rgba(0, 0, 0, .1);
}

.attribution-view>.attribution-info .owner-name {
	color: #000;
	background: #fafafa;
}

/* correct owner name in fullscreen mode */
.attribution-view.is-real-fullscreen {
	background: transparent;
}

/* correct owner name in fullscreen mode */
.attribution-view.is-real-fullscreen>.attribution-info>.owner-name {
	color:#000 !important;
	background:#fff !important;
}

.photo-charm-tags-tag-view>.tags-list>.tag>a {
	color: #000;
	background: #f5f5f5;
}

.photo-charm-tags-tag-view>.tags-list>.tag {
	color: #fff;
	background: #ddd;
}

.photo-charms-view .photo-charms-bar {
	border-bottom: 1px solid #fff;
}

.photo-charms-view .photo-charms-bar>.photo-charm-indicator {
	border-bottom: 1px solid #000;
}

.context-slider-view>.wrapper>.slider>.context-thumb {
	__height: 40px;
	__width: 70px;
	__float: left;
	box-shadow: inset 0 0 0 1px rgba(238, 238, 238, .5);
}

.context-slider-view>.wrapper>.slider>.context-thumb.middle {
	__width: 90px;
	margin-right: 4px;
	margin-left: 4px
}

.context-slider-view>.wrapper>.slider>.context-thumb.currentImage {
	box-shadow: inset 0 0 0 1px #000
}

.context-slider-view>.wrapper>.slider>.context-thumb.emptyImage {
	box-shadow: inset 0 0 0 1px #fff;
	cursor: default
}

.photo-well-view>.navigate-target.navigate-next {
	display: none;
}

.photo-well-view>.navigate-target.navigate-prev {
	display:none;
}

/* not used */
z.photo-well-view {
	__width: 90%;
}

.photo-well-view>.photo-well-media-view {
	__padding-top: 0px;
	-webkit-transform:scale(0.90);
	-moz-transform:scale(0.90);
	transform:scale(0.90);
}

.photo-charms-view .ui-dropdown>span {
	color: #000;
}

/* Fave view background colors */
.fave-view .dialog-tooltip {
	background: #fafafa;
}

/* Reduce font size of fave count */
.fave-view .fave-count {
	font-size: 14px;
}

/* Reduce font size of comment count */
.photo-sidebar-actions-view .button-comment-count {
	font-size: 14px;
}

.photo-sidebar-view a {
	color: #000;
}

.photo-sidebar-view {
	color: #000;
	background: #fafafa;
	__max-width: 20%;
}

.photo-page-view {
	background: #aaa !important;
}

.photo-well-view {
	background: #fff !important;
}

span.editable.meta-field.photo-title.placeholder-text {
	background: #fafafa !important;
	background-color: #fff !important;
	display: none !important;;
}

.ui-dialog {
	background: #f5f5f5 !important;
}

.photo-title-desc-view>.title-desc-block>.meta-field.edit-meta-field {
	 color:#000;
	 background-color:#fff;
	 border: 1px solid transparent;
	 border-bottom: 0px solid #414145;
	 box-shadow: inset 0 0px 0px rgba(10,10,10,0.5);
}

.global-nav-content {
	 display: none;
}

/* get rid of top nav bar when not logged in */
.has-global-nav .photo-well-view {
	 top: 0px;
}

/* get rid of top nav bar when not logged in */
.has-global-nav .photo-sidebar-view {
	 padding-top: 0px;
}

/* get rid of dumb right-click on image overlay */
.photo-well-view>.facade-of-protection-neue {
	display: none;
}

/* force the image to have a top bias in the layout */
/* XXX: comment out if it breaks things */
.photo-well-media-view {
	margin-top: 20px !important;
	margin-bottom: 10% !important;
}

/* more black on white updates */
.comments-view .comments .comment .comment-author a:hover {
	color: #000;
}

.comments-view .comments .comment .comment-date a:hover {
	color: #000;
}

.comments-view .comments-more a:hover {
	color: #000;
}

.comments-view .comments .comment textarea {
	color: #000;
	background: #fff;
}

.ui-dialog-cta-reboot>.ui-dialog-wrapper .ui-dialog-body .ui-dialog-description {
	color: #000;
}

.ui-dialog {
	color: #000;
}

.ui-dialog-modal .album-selection-dialog-view .album-selection-heading, .ui-dialog-modal .album-selection-dialog-view .album-creation-heading {
	color: #000;
}

.ui-charmtabs {
	color: #000;
}

.ui-dropdown>span {
	color: #000;
}

.ui-dialog-modal .confirmation-dialog-view {
	color: #000;
}

.fave-view .dialog-tooltip .ui-dialog-body ul {
	color: #000;
}

.more-menu-view .dialog-tooltip .ui-dialog-body ul li a {
	color: #888;
}

.more-menu-view .dialog-tooltip .ui-dialog-body a:hover {
	color: #000;
}

.more-menu-view .dialog-tooltip .ui-dialog-body ul {
	color: #888;
}

.more-menu-view .dialog-tooltip .ui-dialog-body ul li .comment-control-photo>.commenting-on.selected-comment-permission, .more-menu-view .dialog-tooltip .ui-dialog-body ul li .comment-control-photo .commenting-off.selected-comment-permission {
	color: #000;
}

.share-view .ui-dropdown>ul {
	background-color: rgba(250,250,250,0.95);
}

.share-view .ui-dropdown li {
	color: #888;
}

.ui-dropdown li[data-action="set"]:hover {
	color: #000;
}

.share-view .ui-form legend {
	color: #000;
}

.share-view h1 {
	color: #000;
}

.share-view .ui-form textarea, .share-view .ui-form input {
	background: #fafafa;
	color: #000;
}

.share-view .ui-button {
	background: #fafafa;
	color: #000;
}

.ui-dialog .ui-dialog-body .download-dialog ul.sizes li span.label {
	color: #000;
}

.ui-dialog .ui-dialog-body .download-dialog h2 {
	color: #333;
}

.ui-dialog .ui-dialog-body .download-dialog {
	color: #555;
}

.ui-dialog .ui-dialog-body .download-dialog ul.sizes li span.resolution {
	color: #333;
}

/* kill close button */
.photo-sidebar-view .newCloseBtn {
	 display: none;
}

/* fix misaligned comment count */
.photo-sidebar-actions-view .button-comment-count {
	top: 2px;
}

/* fix misaligned comment icon */
.photo-sidebar-actions-view .ui-icon-comments {
	top: 0px;
}

/* fix funky end-of-line tag stuff */
.photo-charm-tags-tag-view>.tags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag,
.photo-charm-tags-tag-view .machine-tags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag,
.photo-charm-tags-tag-view .autotags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view>.tags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view .machine-tags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view .autotags-list>.tag.can-remove-tag.end-of-the-line>.remove-tag {
    opacity: 0 !important;
    color: transparent !important;
    border: 0px solid #fff !important;
}

/* fix funky end-of-line tag stuff */
.photo-charm-tags-tag-view>.tags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag,
.photo-charm-tags-tag-view .machine-tags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag,
.photo-charm-tags-tag-view .autotags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view>.tags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view .machine-tags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag,
.photo-charm-tags-autotag-view .autotags-list>.tag.can-remove-tag:hover.end-of-the-line>.remove-tag {
    opacity: 1 !important;
    color: #000 !important;
    border: 1px solid #fff !important;
    border-left: none !important;
}


/* black on white date change dialog */
.date-modify-dialog-view.date-modify-view {
     background: #fff;
     color: #000;
}

.date-modify-dialog-view.date-modify-view .date-modify-header {
     background: #fff;
     color: #000;
}

.date-modify-dialog-view .date-inputs div {
     background: #eee;
     color: #000;
}

.date-modify-dialog-view .date-inputs input {
     background: #eee;
     color: #000;
}

.date-modify-dialog-view .date-dropdown {
     background: #eee;
     color: #000;
}


