style_setting_options.value:
/* Beta related edits */

.attribution-view>.attribution-info .owner-name {
background:#ffffff !important;
}

/* stop showing noisy stuff when something isn't in a group or gallery */
.sub-photo-contexts-view .sub-photo-context.empty {
display:none;
}

z.sub-photo-title-desc-view>.title-desc-block {
top:-12px;
padding-top:0px !important;
padding-bottom:0px !important;
}

.sub-photo-right-view {
display: table !important;
}

.sub-photo-right-view .sub-photo-right-row1 {
display: table-caption !important;
border-top: 0px;
border-bottom: 1px solid #eeeeee !important;
}

.sub-photo-right-view .sub-photo-tags-view {
display: table-caption !important;
border-top: 0px solid #eeeeee !important;
border-bottom: 0px solid #eeeeee !important;
}

.sub-photo-right-view .photo-charm-exif-scrappy-view {
display: table-row !important;
border-top: 0px solid #eeeeee !important;
border-bottom: 0px solid #eeeeee !important;
}

.sub-photo-right-view .sub-photo-contexts-view {
display: table-row !important;
border-top: 11px solid #eeeeee !important;
border-bottom: 11px solid #eeeeee !important;
}

.sub-photo-contexts-view .sub-photo-context {
border-top: 1px solid #eeeeee !important;
border-bottom: 0px solid #eeeeee !important;
}

.sub-photo-contexts-view .sub-photo-groups-view {
border-top: 0px solid #eeeeee !important;
border-bottom: 0px solid #eeeeee !important;
}

.sub-photo-contexts-view .sub-photo-albums-view {
__display: table-caption !important;
border-top: 0px solid #eeeeee !important;
border-bottom: 1px solid #eeeeee !important;
}

.sub-photo-contexts-view .sub-photo-galleries-view {
__display: table-caption !important;
border-top: 0px solid #eeeeee !important;
border-bottom:0px;
}

.sub-photo-right-view .sub-photo-additional-info-view {
display: table-footer-group !important;
border-top: 1px solid #eeeeee !important;
border-bottom: 0px;
}

/* still working on these */
.context-slider-scrappy-view {
	background-image:none;
	display:none;
}

.context-slider-scrappy-view .context-slider>.nav {
	-webkit-filter: invert(100%);
}

.context-slider.small-slider {
	display:none;
}

.ui-icon-edit {
	-webkit-filter: invert(15%);
}

.ui-icon-share-24 {
	-webkit-filter: invert(15%);
}

.ui-icon-download {
	-webkit-filter: invert(15%);
}

.photo-sidebar-toggle-view>.toggle>.toggle-icon {
	opacity:0.15;
}

.photo-sidebar-toggle-view>.toggle>.toggle-icon:hover {
	opacity:1.0;
	transition-delay: 0s;
	transition-duration: 0.3s;
	transition-property: opacity;
	transition-timing-function: ease-out;
	-webkit-transition-delay: 0s;
	-webkit-transition-duration: 0.3s;
	-webkit-transition-property: opacity;
	-webkit-transition-timing-function: ease-out;
}


.photo-engagement-view {
	opacity:0.15;
}

.photo-engagement-view:hover {
	opacity:1.0;
	transition-delay: 0s;
	transition-duration: 0.3s;
	transition-property: opacity;
	transition-timing-function: ease-out;
	-webkit-transition-delay: 0s;
	-webkit-transition-duration: 0.3s;
	-webkit-transition-property: opacity;
	-webkit-transition-timing-function: ease-out;
}

.photo-well-scrappy-view .height-controller {
	background:#ffffff;
}

.sub-photo-view {
	background:#ffffff;
}

.sub-photo-view>.sub-photo-container {
	background:#ffffff;
}

.global-nav-shim {
	background:#ffffff;
}

.photo-well-scrappy-view .height-controller .entry-type {
	display:none;
}

.sub-photo-right-view>.sub-photo-right-row1 {
	__border-bottom:0px;
}

.sub-photo-right-view>.sub-photo-tags-view>.sub-photo-tags-people-view {
	display:none !important;
}

.sub-photo-tags-tag-view {
	width:500px !important;
}

.sub-photo-keyboard-shortcuts-view {
	display:none;
}

.footer-full-view {
	display:none;
}

.sub-photo-tags-tag-view>.tags-list>.tag>a, .sub-photo-tags-tag-view .machine-tags-list>.tag>a {
	padding:0 10px 0px;
	font-weight: 300;
	font-size:85%;
}
/* end beta */
