css:
@-moz-document url-prefix("http://www.tumblr"), url-prefix("https://www.tumblr") {
.layout_iframe {
background: transparent !important;
}

body {
height: 100%;
background: #090909 !important;
color: #444;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: #000 !important;
}

#pagination a {
color: #888;
background-color: rgba(0,0,0,0);
}

#pagination a:hover {
color: #ccc;
}

#tumblr_radar {
visibility: hidden;
}

.footer_links a {
color: #555;
}

.footer_links a:hover {
color: #999;
}

.post.new_post .new_post_label {
font-size: 15px;
}


#right_column:after {
visibility: hidden;
}

div.l-header .selection_nipple {
border-bottom: 12px solid #000 !important;
}

#tab_switching .tab_notice, div.l-header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
background-color: #00f !important;
}

.right_column {
background: #090909;
}
  
.pre_as_input, input.text_field, textarea {
color: #ccc;
background: #111;
}

.chrome.blue {
border-color: #888;
background: -webkit-linear-gradient(top,#558 0,#88b 100%);
}

select {
background-color: black;
color: white;
}

.chrome.white {
color: rgba(0,0,0,.6);
background: -webkit-linear-gradient(top,#000 0,#800a0a 25%,#800a0a 75%,#000 100%);
border-color: #bfbfbf;
}

.pill.notice {
color:#000;
}
}

@-moz-document url-prefix("https://www.tumblr"), url-prefix("http://www.tumblr") {
#xim-add-person {
background: #0c0c0c;
}




::-webkit-scrollbar {
width: 12px;
}
 
::-webkit-scrollbar-track {
box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
border-radius: 10px;
}
 
::-webkit-scrollbar-thumb {
border-radius: 10px;
-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5);
background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#888), color-stop(50%,#aaa), color-stop(100%,#888));
}

::-webkit-scrollbar-button {
background: #333;
}

::-webkit-scrollbar-track-piece {
background: #333;
}
}

@-moz-document url-prefix("http://www.tumblr"), url-prefix("https://www.tumblr") {
#search_controls .arrow{
background: url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png") -100px -175px;
}




.user_menu_info{
background: url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png") -120px -220px;
}


.user_menu .user_menu_icon{
background: url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png") 0 0;
}



.tab_notice_nipple {
background: url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png") -90px -100px !important;
}


.post_full .post_permalink {
background: url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png") -0px -90px !important;
}




#nav .nav_item div .nav_item_menu_arrow,#search_box .search_icon,#search_controls .arrow,#search_controls .check,.user_menu_info,.user_menu .user_menu_icon,.tab_notice_nipple {
background-image:url("http://i1314.photobucket.com/albums/t577/DistortedOutsider/Tumblr%20sprites/SpriteSheetRedesignCroppedVar01Ver01_zpsca81f11e.png");
}


div.l-header .selection_nipple.white {
background-image: url("http://media.tumblr.com/fedf4c9be4cc7e2d6d3789a0e7fdb0ef/tumblr_inline_mnd32rqQBN1rlx4pg.png") !important;
background-position:  -80px -60px !important;
}
}

@-moz-document url-prefix("http://assets.tumblr.com"), url-prefix("https://assets.tumblr.com") {
pre, xmp, plaintext, listing {
background-color: #fff !important;
color: #000 !important;
}
}
