css:
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix('https://www.tumblr.com/dashboard')   {
.with_auto_paginate #home_button, .with_auto_paginate #inbox_button{
  position: fixed !important;
  top:20px;
  right:20px;
  width:50px;
  padding:0 !important;
}
.with_auto_paginate #home_button a, .with_auto_paginate #inbox_button a{
  margin:0 auto;
}
.with_auto_paginate #home_button{
  margin-top:70px;
}
.with_auto_paginate #inbox_button{
  margin-top:120px;
}
.with_auto_paginate .selection_nipple{
  display:none !important;
}
}
