#navi {
  		position:absolute;
  		position:fixed!important;
        background-color:#000000!important;
        background-repeat:repeat-x!important;
        margin:-73px 0px 0px 0px;
        border:none!important;
        z-index:9000!important;
        min-width: 550px !important;
  		max-width: 800px !important;
        width:370px!important;
        min-height:5px!important;
        max-height: 100px!important;
  		box-shadow: 4px 2px 6px #000000, -4px -2px 6px #000000;
}
#logo_panel {
margin-top:30px!important;  
}


html{
    background-image: url("http://wallpoper.com/images/00/21/11/89/technology-hacker_00211189.jpg");
    background-size:100%!important;
    }



#mobtop{
display:none!important;
}
#reklama{
display:none!important;
}

div[style="padding:3px;border:1px solid #E2C822;"]{
padding: 41px!important;
}

div[style="text-align:center;font-size:small"]:hover{
opacity:0.9!important; 
}
#navi a:link, #navi a:visited{
   
   -moz-transition:-moz-transform 0.2s ease;
   -webkit-transition:-webkit-transform 0.2s ease;
}

  
div.content_wrap{
box-shadow: 4px 2px 6px #168517, -4px -2px 6px #168517;
}
  
button, input[type="button"], input[type="submit"], input[type="reset"], .button{
-webkit-gradient(linear,left top,left bottom,from(rgb(18,131,20)),to(rgb(32, 33, 34)))!important;
}

    .moders_header_block {
