}

input[type=submit]{
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.5)!important;
border-radius:20px;
	-moz-appearance: none!important;
font-family:Comic Sans MS!important;
margin-left:5px;
margin-bottom:5px;
