css:
#navi {
  		position:absolute;
  		position:fixed!important;
        background-color:#000000!important;
        background-repeat:repeat-x!important;
        margin:-73px 0px 0px 0px;
        border:none!important;
        z-index:9000!important;
        min-width: 550px !important;
  		max-width: 800px !important;
        width:370px!important;
        min-height:5px!important;
        max-height: 100px!important;
  		box-shadow: 4px 2px 6px #D9ABF9, -4px -2px 6px #D9ABF9;
}
#logo_panel {
margin-top:30px!important;  
}


html{
    background-image: url("http://www.youloveit.ru/uploads/gallery/main/456/mlp_wallpaper_rarity18.png");
    background-size:100%!important;
  	background-attachment:fixed;
    }



#mobtop{
display:none!important;
}
#reklama{
display:none!important;
}

div[style="padding:3px;border:1px solid #E2C822;"]{
padding: 41px!important;
}

div[style="text-align:center;font-size:small"]:hover{
opacity:0.9!important; 
}
#navi a:link, #navi a:visited{
   
   -moz-transition:-moz-transform 0.2s ease;
   -webkit-transition:-webkit-transform 0.2s ease;
}

  
div.content_wrap{
box-shadow: 10px 8px 10px #8258A0, -10px -8px 10px #8258A0;
}
  
button, input[type="button"], input[type="submit"], input[type="reset"], .button{
-webkit-gradient(linear,left top,left bottom,from(rgb(124,81,153)),to(rgb(130,88,160)))!important;
}

    .moders_header_block {
background: rgba(130,88,160, 0.10);
}
.odd_mail {
background: rgba(130,88,160, 0.25);
}

.light_blue {
background: rgba(130,88,160, 0.20);
}

input[type=submit]{
background: none repeat scroll 0 0 rgba(130,88,160, 0.5)!important;
border-radius:20px;
	-moz-appearance: none!important;
font-family:Comic Sans MS!important;
margin-left:5px;
margin-bottom:5px;
margin-top:3px;
-moz-transition:background 0.5s ease;
}
input[type=submit]:hover{
background: none repeat scroll 0 0 rgba(130,88,160, 1) !important;

}
a.red_link, a.red_link span, .red_item {
color: rgb(130,88,160);
}
.newevent {
color: rgb(0, 41, 255);
font-size: medium;
vertical-align: bottom;
}

.menu_wrap, .touch_menu .tm_link {

background: -webkit-gradient(linear,left top,left bottom,from(rgb(255, 255, 255)),to(rgb(174, 115, 230)));

}

.many_events .newevent {
color: rgb(68, 48, 213);
}
.bottom_link_block, .author_widget, .search_bar, div.backlink, .blue_bg {
background: rgba(217, 171, 249, 0.25);
}
#navi_footer a:link, #navi_footer a:visited {
color: rgb(0, 0, 0);
text-decoration: none;
font-size: small;
}
#navi_footer a:focus, #navi_footer a:hover, #navi_footer a:active {
color: rgb(14, 14, 14);
background-color: rgba(0, 0, 0, 0.35);
}
#navi_footer {
color: rgb(255, 255, 255);
font-size: small;
background-color: rgba(217, 171, 249, 0.47);
border: #D9ABF9;
}

#navi a:link, #navi a:visited{
   -moz-transition:-moz-transform 0.2s ease;
   -webkit-transition:-webkit-transform 0.2s ease;
}
#navi a:focus, #navi a:hover, #navi a:active{
   background:transparent!important;
    
}
    #navi table a, .touch_menu .tm_link .tm_link_inner {
border-left: 1px solid #D9ABF9;
border-right: 1px solid #D9ABF9;
border-bottom: 1px solid #D9ABF9;
border-top: 1px solid #D9ABF9;
}
    
.busi_switcher table a {
background: -webkit-gradient(linear,left top,left bottom,from(rgb(255, 255, 255)),to(rgb(174, 115, 230)));
}

.location_bar {
background: rgba(217, 171, 249, 0.4);
}
input[type=text]{
  border-radius:10px;
	-moz-appearance: none!important;
box-shadow: 1px 1px 1px #8258A0, -1px -1px 1px #8258A0;
  }
textarea[name=text]{
  border-radius:5px;
	-moz-appearance: none!important;
box-shadow: 2px 2px 2px #8258A0, -2px -2px 2px #8258A0;
  }
textarea[name=body]{
box-shadow: 2px 2px 2px #8258A0, -2px -2px 2px #8258A0;
  }


.widget_title_block {
background: rgb(183, 104, 238);
}
.dark_bg {
background: rgb(167, 81, 228);
}
textarea [id=textarea][name=body]{
  border-radius:5px;
	-moz-appearance: none!important;
box-shadow: 2px 2px 2px #8258A0, -2px -2px 2px #8258A0;
  }
