css:
@-moz-document domain(tumblr.com) {
#logo_panel {
   		position:absolute;
        position:fixed!important;
        box-shadow:4px 2px 6px #8FA4B4, -4px -2px 6px #8FA4B4, 4px -4px 6px #8FA4B4, -4px -4px 6px #8FA4B4!important;
        border-radius:3px; 
        margin:-67px 0px 0px 0px!important;
        z-index:9000!important;
        min-height:5px!important;
        max-height: 100px!important;
        min-width:528px;
        max-width:529px;
        
	
}

#navi {
  		position:absolute;
        position:fixed!important;
        background-color:#000000!important;
        background-repeat:repeat-x!important;
        margin:-30px 0px 0px 0px!important;
        box-shadow:4px 2px 6px #8FA4B4, -4px -2px 6px #8FA4B4, 4px -4px 6px #8FA4B4, -4px -4px 6px #8FA4B4!important;
        border-radius:3px;
        z-index:9000!important;
        min-width: 548px !important;
  		max-width: 600px !important;
        width:370px!important;
        min-height:5px!important;
        max-height: 100px!important;
}

.moders_header_block {
        color: #000000!important;
        font-family: Colibri!important;
        text-shadow:3px 1px 5px!important;
        font-size: 17px;
        background-color:#E5E5E5;

}

a:hover {
    text-shadow: 1px 0px 5px!important;
}


.location_bar, .author_widget{   
        color: #000000!important;
        font-family: Comic Sans MS!important;
        margin:-15px 0px -5px 0px!important;
        border-radius:3px;

        min-width: 528px !important;
  		max-width: 600px !important;
        width:350px!important;
        min-height:5px!important;
        max-height: 100px!important;
        font-size:large!important;
    text-shadow:3px 1px 5px!important;
  
}
  
 .service_links_block {
color: #068D06;
}
  
.bottom_link_block {
    color: #000000!important;
    font-family: Comic Sans MS!important;
    font-size:small!important;
    text-shadow:3px 1px 5px!important;   
}





input[type=submit]{
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.5)!important;
border-radius:20px;
	-moz-appearance: none!important;
font-family:Comic Sans MS!important;
margin-left:5px;
margin-bottom:5px;
margin-top:3px;
-moz-transition:background 0.5s ease;
}
input[type=submit]:hover{
background: none repeat scroll 0 0 rgba(1,10,10, 10) !important;
}

.main{
   opacity:0.9!important;
   font-family: Arial!important;
   box-shadow:4px 2px 6px #8FA4B4, -4px -2px 6px #8FA4B4, 4px -4px 6px #8FA4B4, -4px -4px 6px #8FA4B4!important;
   margin-bottom:5px
   }


  
  
html{
    background-repeat:no-repeat!important;
    background-attachment:fixed!important;
    overflow-y: scroll;
    margin:70px 0px 0px 0px!important;
    background-color:black;
    background-size:100%!important;
    box-shadow:4px 2px 6px #8FA4B4, -4px -2px 6px #8FA4B4, 4px -4px 6px #8FA4B4, -4px -4px 6px #8FA4B4!important;
    cursor:url(http://cur.cursors-4u.net/cursors/cur-2/cur116.cur),default;
} 
a:link, a:visited {
    color: #006699;
cursor:url(http://cur.cursors-4u.net/cursors/cur-2/cur117.cur),default;
}


#mobtop{
display:none!important;
}
    
#reklama{
display:none!important;
}




    div.content_wrap{
box-shadow:4px 2px 6px #8FA4B4, -4px -2px 6px #8FA4B4, 4px -4px 6px #8FA4B4, -4px -4px 6px #8FA4B4!important;
}
}
