css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com"), url-prefix("http://facebook.com"), url-prefix("https://facebook.com") {

body.fbIndex { 

background: url('http://i634.photobucket.com/albums/uu65/psandme/Post/May%2026/7DefaultWallpaperNormal.png') !important; 

-webkit-background-size: cover; 

-moz-background-size: 100% !important; 

background-repeat: no-repeat !important;

background-size: cover !important;

}



.loggedout_menubar_container { background: url('http://fiakka.com/images/blank_transparent.png') !important; }

.fbIndex #globalContainer #dropmenu_container,

.fbIndex #globalContainer #content,

.fbIndex #globalContainer #pageFooter { display: none !important }





.fbIndex .loggedout_menubar_container {

  position: fixed !important;

  width: 420px !important;

  height: 82px !important;

  min-width: 0 !important;

  top: 50% !important;

  left: 50% !important;

  margin-top: -17px !important;

  margin-left: -210px !important;

  z-index: -1 !important;

}





.fbIndex .loggedout_menubar { width: auto !important }

.fbIndex .loggedout_menubar_container .lfloat,

.fbIndex .loggedout_menubar_container .rfloat { float: none !important }

.fbIndex .loggedout_menubar_container .lfloat img,

.fbIndex .loggedout_menubar_container .rfloat #login_form table { display: block !important; margin: 0 auto !important }



.fbIndex .loggedout_menubar_container .lfloat img { display: block; margin: -60px auto 20px !important; }







#SetAsHomepage_Callout {

  display: none;

}



i.fb_logo {

display: none !important;

}



#login_form {

position: fixed;

top: 80px;

height: 420px;

width: 450px;

background: url('http://i45.tinypic.com/69qcyh.png');

left: 50%;

margin-left: -225px !important;

}



#login_form .uiButtonConfirm {

position: fixed;

top: 330px;

height: 25px;

width: 20px;

left: 50%;

margin-left: 150px;

background: url('http://i48.tinypic.com/346n6li.png') !important;

text-indent: -9999px;

border: none !Important;

-webkit-background-size: cover;

-moz-background-size: cover;

display: block !important;

}



#login_form table {

position: fixed;

top: 300px;

float: none !important;

}





#login_form tbody {

left: 50%;

width: 342px;

margin-left: -110px;

position: fixed;

float: none !important;

}



#login_form tbody #pass {

position: absolute !important;

top: 56px;

left: 15px

}



#login_form tbody #email {

position: absolute !important;

top: 20px;

left: 15px

}





#login_form tbody label , #login_form tbody #persist_box {

display: none !important;

}



#login_form tbody input {

width: 214px !important;

}



#login_form label , #login_form a {

color: white;

display: none !important;

}

}
