description:
It:
- Changes background color/image;
- Removes rounded borders;
- Changes sidebar colors;

css:
body {
  background-image: url('http://lh3.ggpht.com/-78UQU-9qm9I/UaV9TM-F7OI/AAAAAAAACjA/JmNwvxTpLpY/s550/kurok.png') !important;
  background-position:bottom left !important;
  background-color:rgba(200,230,235,.7) !important;
  background-repeat:no-repeat !important;
  background-attachment:fixed !important;
}

.plexi.show.color {
  background-color:rgba(200,230,235,.7) !important;
}

#content {
  background:rgba(255,255,255,.3)!important;
  border-radius:0px !important;
}

.selection_nipple {
  border-bottom-color:rgba(255,255,255,.3) !important;
  margin-top:-1px !important;
}

#posts .post, #posts .post_avatar_link {
  border-radius:0px !important;
}

.post_full .post_permalink {
  visibility:hidden !important;
}

#header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
  background-color:rgba(170,200,220,1) !important;
  box-shadow:0px 0px 0px !important;
  margin-left:-5px !important;
  margin-top:5px !important;
}

.tab_notice_value {
  box-shadow:0px 0px 0px !important;
}

.post{
  box-shadow:0px 0px 2px rgba(0,0,0,.3) !important;
}

.arrow {
  visibility:hidden !important;
}

.notification .avatar_frame, .notification .notification_right .preview_frame {
  border:1px solid rgba(90,100,120,.4)!important;
  border-radius:0px !important;
}

.controls_section {
  background-color:rgba(85,150,200,.5) !important;
  border-radius:0px !important;
  border:1px solid rgba(0,0,0,.04) !important;
}

.controls_section a {
  color:rgba(255,255,255,.7)!important;
}

#tumblr_radar {
  visibility:hidden!important;
  overflow:hidden !important;
}
  
#right_column {
  background:transparent !important;
  height:auto!important;
  overflow-y:hidden !important;
}

.tab_notice_nipple {
  visibility:hidden !important;
}

#tab_switching .tab_notice::after, #header .tab .tab_notice::after, .selected_blog .tab_notice::after {
  border:1px solid rgba(0,0,0,.06) !important;
  box-shadow:0 0 0 rgba(0,0,0,.08) !important;
}

.notification_sentence a{
  color:rgba(130,150,170,.9)!important;
}

.notification_sentence {
  color:rgba(90,100,120,1)!important;
  border-radius:0px !important;
}
    
.notification, .notification_inner {
  border-radius:0px !important;
}

.notification_inner {
  background:rgba(235,245,245,.6)!important;
}

.popover_inner {
  border-radius:0px !important;
  width:213px !important;
  margin:0px !important;
  box-shadow:0px 0px 10px rgba(0,0,0,.9) !important;
}

.controls_section .open_blog.with_subtitle {
  background:rgba(105,170,220,.4) !important;
  box-shadow:0px 1px 3px (0,0,0,.7) !important;
  padding-left:15px !important;
}

#auto_pagination_loader {
  background:rgba(85,150,200,.4) !important;
  text-shadow:1px 1px 1px rgba(85,150,200,.9) !important;
  color:rgba(255,255,255,1) !important;
}

#auto_pagination_loader a {
  color:rgba(255,255,255,1) !important;
  text-shadow:1px 1px 1px rgba(85,150,200,.9) !important;
}

#auto_pagination_loader img {
  opacity:0!important;
}
