css:
@-moz-document domain(pathofexile.com) {

.forumPostListTable tr{
    background-image: none !important;
    background-color: #FFF !important;
    }
    
.forumPostListTable tr.even{
    background-image: none;
    background-color: #F7F7F8 ;
    }

.forumPostListTable .staff,.forumPostListTable .staff.even{
    background-color: #FFE8AB !important;
    }

.formForumPost table {
    background-color: #FFF !important;
    color: #000 !important;
    }
    
.formForumPost input {
    background-color: #FFF !important;
    color: #000 !important;
    }
    
.formForumPost textarea {
    background-color: #fff !important;
    color: #000 !important;
    }
    
.forumTable tr blockquote {
    background-color: #F7F7F8 !important;
    }
    
.forumTable tr.even blockquote {
    background-color: #FFF !important;
    }
    
.forumTable a {
    color: #000 !important;
    }
    
.forumTable blockquote a {
    color: #000 !important;
    }
    
.forumTable .signature {
    color: #000 !important;
    }

.forumTable td {
    color:  #000 !important;
}

.forumTable blockquote{
    color: #000 !important;
}

.spoiler .spoilerTitle, .spoiler .spoilerContent{
    background-image: none !important;
    background-color: #fff !important;
    }

.spoiler input{
    background: #FFF !important;
    color: #000 !important;
    }
    
.itemInfoBox, .itemLeaguePopup, .newItemContainer .verifiedStatus {
    background-color: #FFF !important;
    color: #000 !important;
}

.layoutBox1.forumTheme .breadcrumb {
    background-color: #FFF;
    color: #000 !important;
}
.layoutBox1.forumTheme .breadcrumb a {
    color: #000 !important;
    }
    
.layoutBox1.forumTheme .botBar.first {
    background-color: #FFF !important;
    background-image: none !important;
    box-shadow: none !important;
    color: #000 !important;
    }
    
.layoutBox1.forumTheme .topBar {
    background-color: #FFF !important;
    background: #FFF !important;
    box-shadow: none !important;
    color: #000 !important;
    }
    
.layoutBox1.forumTheme .topBar.last a{
    color: #000 !important;
    background-color: #FFF !important;
    }
    
.layoutBox1.forumTheme .botBar.first a{
    color: #000 !important;
    background-color: #fff !important;
    }
    
div.forumSortContainer {
    background-color: #fff !important;
    background: #fff !important;
    color: #000 !important;
    }
    
div.forumSortContainer a {
    color: #000 !important;
    background-color: #fff !important;
    }
    
.forumSearchForm input.textInput {
    color: #000 !important;
    }

.layoutBox1.forumTheme .layoutBoxTitle{
    background-image: none !important;
    background-color: #fff !important;
    box-shadow: none !important;
    color: #000 !important;
    }
    
.forumTable tr {
    background-color: #F7F7F8 !important;
    }
    
.forumTable tr.even {
    background-color: #fff !important;
    }
    
.forumTable strong {
    color: #000 !important;
    }

.forum_pagination a {
    background-color: #fff !important;
    color: #000 !important;
    }
    
.forumTable th {
    background-image: none !important;
    background-color: #fff !important;
    color: #000 !important;
    }

.clear_viewed, .setReadButton {
    background: #FFF !important;
    color: #000 !important;
    border: 1px solid #000;
}

/*div.layoutBoxContent {
 *   background: #FFF;
 *   background-color: #FFF;
 *   }
 */
 
.editor-control {
    color: #000 !important;
    background-color: #fff !important;
    }

.viewForumTable .heading {
    display:none;
    }

.forumIndexTable .heading {
    display:none;
    }

}
