css:
@-moz-document 
url-prefix(https://theoldreader.com),
url-prefix(http://theoldreader.com),
url-prefix(https://www.theoldreader.com),
url-prefix(http://www.theoldreader.com) {

a { color: #1155CC; }
.content-body p { font-sie:14px !important; line-height:19px !important; }
.nav-list.feeds-list { margin-left: 20px; }
p { margin-bottom:20px !important; }
.post, .post.unread { border:1px solid #e9e9e9;  }
.post h3 a { display:block; font-size:1.1em; margin-bottom:15px !important; margin-top: 15px; color: #000; }
.post.unread h3 a { display:block; font-size:1.1em; margin-bottom:15px !important; margin-top: 15px; color: #1155CC; }
.content-body { max-width: 700px; }
.content-body p img { display:block; margin-bottom:6px; }
.content-body a img { display:inline; margin-bottom:0px; }
blockquote { border-radius:10px; padding:10px 20px !important; border:0px; margin-top:10px; margin-bottom:10px; }
blockquote p { font-weight:normal; line-height:1.5em; }
.post .content a { color: #000; text-decoration: underline; }
.post .content a:hover { text-decoration: none; }
.post.unread .content a { color: #1155CC; }
.post.unread .content a.label-feed { color: #fff; text-decoration: none; }
.well { background: #fff; }
.nav-header { text-transform: none; }
.nav-list li.nav-header a { font-size: 13px; }
.nav-list li.nav-header a, 
.nav-list li.nav-header a:hover { color: #222 !important; }
.nav-list li:hover { color: #222; background: #eee;}
.nav-list li a:hover { color: #222; }
.nav-list strong { font-weight: bold; color: #222; }
.nav-list .active strong { color: #DD4B39; }
.label { border-radius: 0; -webkit-border-radius: 0; text-shadow: 0 1px 0 rgba(0,0,0,0.25); }
.label.label-feed { background: #ccc; }
.badge.badge-info { background: none; color: #333; font-weight: normal; font-size: 11px; }
.badge.badge-info:before { content: "("; }
.badge.badge-info:after { content: ")"; }
.nav-list .badge { margin: 0; }
.nano>.pane>.slider { background: #ddd; }
}
