css:
.post_full .post_footer {
  width: 200px !important;
  float: right !important;
  position: absolute !important;
  top: 0 !important;
  right: 0 !important;
  border: none !important;
  margin-top: 0 !important;
  margin-right: 25px !important;
}

.post_full .post_notes, .post_full .post_notes {
  width: 75px !important;
  display: inline-block !important;
  margin: 0 !important;
  padding: 0 !important;  
}

.post_full .post_controls, .post_full .post_controls {
  width: 125px !important;
  display: inline-block !important;
  margin: 0 !important;
  padding: 0 !important;  
}

.post_full .post_header {
  margin-bottom: 10px !important;
  height: 50px !important;
}

.post_full .post_source {
  margin-top: 20px;
  left: 0px;
  position: absolute;
  display: block !important;
  float: left !important;
}

.post .post_header a {
  max-width: 100% !important;
}

.post_full .post_info_fence {
  max-width: 100% !important;
  display: block !important;
}

.post_avatar .post_avatar_link {
  border-radius: 0 !important;
}

.post .post_info .reblog_follow_button {
  display: none !important;
}
