css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zippyshare.com") {

body{
background-color: #131313!important;
overflow: hidden!important;
}

*{
color: #777!important;
margin: 0;
}

a{
color: #777!important;
}

.outer_menu,
.outer_main{
background-color: #252525!important;
box-shadow: 0px 1px 0px #333, inset 0px 2px 8px #151515!important;
border: solid 1px #151515!important;
}

.inner_menu,
.inner_main{
background-color: transparent!important;
color: #555!important;
}

.outer_menu{
margin-bottom: 30px!important;
margin-top: 0!important;
}

ul.menu > li{
background-color: #333!important;
border-color: #444!important;
}

ul.menu > li.active{
background-color: #444!important;
border-color: #444!important;
}

.content{
background: transparent!important;
}

#urlsNew > .content > textarea{
background-color: #333!important;
border-color: #444!important;
}

a.menu{
color: #555!important;
text-shadow: 0px -1px 0px #777, 0px 2px 1px #101010!important;
transition: 0.5s!important;
}

a.menu:hover{
color: darkcyan!important;
text-shadow: 0px -1px 0px cyan, 0px 2px 1px #101010!important;
transition: 0.5s!important;
}

#lang-one > li > ul{
background-color: transparent!important;
border: none!important;
padding-top: 5px!important;
}

#lang-one > li > a{
background-color: #333!important;
border-radius: 30px!important;
}

#lang-one > li{
border-radius: 30px!important;
border-color: #444!important;
}

#lang-one > li > ul > li > a{
border-color: #444!important;
background-color: #222!important;
}

#lang-one > li > ul > li:first-child > a{
border-radius: 15px 15px 0px 0px!important;
border-color: #444!important;
background-color: #222!important;
}

#lang-one > li > ul > li:first-child{
border-radius: 15px 15px 0px 0px!important;
}

#lang-one > li > ul > li:last-child > a{
border-radius: 0px 0px 15px 15px!important;
border-color: #444!important;
background-color: #222!important;
border-bottom: solid 1px #444!important;
}

#lang-one > li > ul > li:last-child{
border-radius: 0px 0px 15px 15px!important;
}

.inner_main > table {
background: url(http://image-upload.de/image/EpnHWO/de860000ed.png) no-repeat scroll right top !important;
}

.text_field{
color: red!important;
}

#uploadify{
  width: 250px!important;
  height: 100px!important;
  margin-top: 30px!important;
  margin-bottom: 30px!important;
  
}
  
#uploadify-button,
#uploadify > object{
  height: 100%!important;
  width: 100%!important;
  background-image: none!important;
}
  
#uploadify-button{
  background-image: url("http://i.imgur.com/eZL4dTI.png")!important;
  cursor: pointer!important;
  z-index: 10!important;
  border-radius: 10px!important;
  box-shadow: 0 0 0 2px rgba(0, 220, 250, 0.5),
    0 0 20px -10px rgba(0, 220, 250, 1)!important;
  transition: 0.3s;
}

[id^="SWFUpload_0_"]{
border-color: #444!important;
box-shadow: 0px 1px 0px rgba(0, 220, 250, 1), 0px 5px 4px -3px rgba(0, 220, 250, 0.5)!important;
margin-top: 20px!important;
margin-bottom: 20px!important;
background-color: #333;
}

.container > div > a > img[src*="/images/logo.png"]{
height: 0 !important;
width: 0 !important;
padding-left: 350px !important;
padding-top: 176px !important;
margin-bottom: 0!important;
background: url(http://i.imgur.com/0G7Lpn5.png) no-repeat !important;
}
  
#share_button[src*="/images/upload_small.png"]{
height: 0 !important;
width: 0 !important;
padding-left: 180px !important;
padding-top: 60px !important;
background: url(http://image-upload.de/image/NTjY9I/fa15f7dab6.png) no-repeat !important;
}

#share_button[src*="/images/upload_small.png"]:active,
#share_button[src*="/images/upload_small.png"]:hover{
height: 0 !important;
width: 0 !important;
padding-left: 180px !important;
padding-top: 60px !important;
background: url(http://image-upload.de/image/aHYCFh/01a38a9e7e.png) no-repeat !important;
cursor: pointer!important;
}

#dlbutton{
margin-left: 260px!important;
}


#lrbox > .right > #dlbutton{
margin-left: -50px!important;
}

#dlbutton > img{
margin-top: 40px!important;
margin-bottom: 20px!important;
}

#dlbutton > img[src*="/images/download_small.png"]{
height: 0 !important;
width: 0 !important;
padding-left: 180px !important;
padding-top: 60px !important;
background: url(http://image-upload.de/image/T6jtRj/eb78c9aa5d.png) no-repeat !important;
}

#dlbutton > img[src*="/images/download_small.png"]:active,
#dlbutton > img[src*="/images/download_small.png"]:hover{
height: 0 !important;
width: 0 !important;
padding-left: 180px !important;
padding-top: 60px !important;
background: url(http://image-upload.de/image/t60Ujg/ec6ea86c42.png) no-repeat !important;
cursor: pointer!important;
}

.left > font > img{
width: 400px!important;
}

#dlbutton > img[src*="/images/download.png"]{
height: 0 !important;
width: 0 !important;
padding-left: 292px !important;
padding-top: 106px !important;
background: url(http://image-upload.de/image/RKZ6yQ/bddeb501e8.png) no-repeat !important;
}

#dlbutton > img[src*="/images/download.png"]:active,
#dlbutton > img[src*="/images/download.png"]:hover{
height: 0 !important;
width: 0 !important;
padding-left: 292px !important;
padding-top: 106px !important;
background: url(http://image-upload.de/image/4i7Dxu/2ab58832b1.png) no-repeat !important;
cursor: pointer!important;
}

.uploadify-progress{
background-color: #222!important;
border-color: #444!important;
}

.uploadify-progress-bar{
background-color: rgba(0, 220, 250, 1)!important;
box-shadow: 0px 0px 2px 1px rgba(0, 220, 250, 0.5)!important;
}

#mpl{
border-radius: 10px!important;
}

.login{
background-color: #222!important;
padding-left: 60px!important;
padding-top: 10px!important;
padding-bottom: 10px!important;
padding-right: 60px!important;
margin-right: -710px!important;
margin-top: 10px!important;
border-radius: 20px 0px 0px 20px!important;
background-image: url("http://i.imgur.com/sk6oJqU.png")!important;
background-repeat: no-repeat!important;
background-position: 13px 15px!important;
transition: 0.6s!important;
}

.login:hover{
background-color: #222!important;
padding-left: 60px!important;
padding-top: 10px!important;
padding-bottom: 10px!important;
padding-right: 60px!important;
margin-right: -40px!important;
margin-top: 10px!important;
border-radius: 20px 0px 0px 20px!important;
background-image: url(http://image-upload.de/image/Uw4uS3/fb4d32ce37.png)!important;
background-repeat: no-repeat!important;
background-position: 13px 15px!important;
transition: 0.6s!important;
}

}
