css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.youtube.com/watch"), url-prefix("https://www.youtube.com/watch") {

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/Hgo13k-tfSpn0qi1SFdUfT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), local('Roboto-Regular'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 700;
  src: local('Roboto Bold'), local('Roboto-Bold'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/d-6IYplOFocCacKzxwXSOD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 300;
  src: local('Roboto Light Italic'), local('Roboto-LightItalic'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/7m8l7TlFO-S3VkhHuR0at4bN6UDyHWBl620a-IRfuBk.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 400;
  src: local('Roboto Italic'), local('Roboto-Italic'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/1pO9eUAp8pSF8VnRTP3xnvesZW2xOQ-xsNqO47m55DA.woff) format('woff');
}
@font-face {
  font-family: 'Roboto';
  font-style: italic;
  font-weight: 700;
  src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(http://themes.googleusercontent.com/static/fonts/roboto/v8/t6Nd4cfPRhZP44Q5QAjcC4bN6UDyHWBl620a-IRfuBk.woff) format('woff');
}

html, body {
    font-family: "Roboto", Arial, Helvetica, sans-serif !important;
    background: #eeeeee !important;
}

#yt-masthead-container {
    background: #fcfcfc !important;
    border: 0 !important;
    box-shadow: 0 1px 2px rgba(0,0,0,.2);
    position: fixed;
    width: 100% !important;
    z-index: 999;
}
#yt-masthead a.yt-uix-button {
    line-height: 24px !important;
}
#yt-masthead #logo-container {
    margin-right: 184px !important;
}

body #masthead-expanded-container {
    padding-top: 65px !important;
    background: #444444 !important;
    border-bottom: 1px solid #333333 !important;
}
body #masthead-expanded-container a:link, body #masthead-expanded-container a:visited { color: #bfbfbf !important; }
.yt-uix-button-link { color: #bfbfbf !important; }

#page {
    padding-top: 75px !important;
}
#page.watch #guide-container {
    margin: 0 0 10px 5px !important;
    padding-bottom: 0 !important;
    background: #ffffff !important;
    border-radius: 1px;
    /* border: 1px solid #e6e6e6 !important; */
    box-shadow: 0 1px 1px #dbdbdb;
}
.guide-module {
    min-height: 820px !important;
}
.guide-module.collapsed {
    min-height: 23px !important;
}
.guide-channels-list { max-height: 491px !important; }

#player-api { z-index: 900 !important; }

.player-width { width: 850px !important; }
.player-height { height: 508px !important; }
.transition-content.watch-wide .watch-medium, #watch7-main-container {
    width: 1280px !important;
}
.transition-content.watch-wide .watch-medium .player-width { width: 1280px !important; }
.transition-content.watch-wide .watch-medium .player-height { height: 750px !important; }
.site-left-aligned.guide-enabled #player, .site-left-aligned.guide-enabled #watch7-main-container {
    padding-left: 310px !important;
}
#watch7-content { width: 850px !important; }
#watch7-sidebar {
    margin-left: 850px !important;
    margin-top: -508px !important;
    background: #f8f8f8 !important;
    border: 1px solid #e6e6e6 !important;
    border-left: 0 !important;
    padding-top: 20px !important;
}
.watch-wide #watch7-sidebar, .watch-playlist #watch7-sidebar, .watch-branded #watch7-sidebar {
    z-index: 1 !important;
    margin-top: 0 !important;
}
.watch-content { width: 850px !important; }
.watch-sidebar { max-width: 430px !important;  }
.watch7-playlist-bar-right { width: 430px !important; }

.watch-medium #player-api.player-width { width: 1280px !important; }
.watch-medium #player-api.player-height { height: 750px !important; }

}
