#commonDialog, #aboutDialog, .groupbox-title, caption, dialog #tabpanels, dialog tab,
filefield, #print-preview-toolbar, listbox 
{ -moz-appearance: -moz-win-glass !important;
background: rgba(150,150,150,.4) !important; }

#selectEditMenuItems
{ -moz-appearance: none !important;
background: rgba(150,150,150,.7) !important; }

dialog, #commonDialog, dialog tabpanels, .text-link, listbox
{ font-weight: bold !important;
color: black !important;
text-shadow: 1px 2px 1px #999 !important; }

/* === dialog windows tabs */
dialog > tabbox > tabs > tab
{ -moz-appearance: none !important;
border-radius: 3px !important;
background: radial-gradient(at top, #A0AA8E, transparent), linear-gradient(to bottom,rgba(150,150,150, .6), rgba(150,150,150, .2), transparent) !important;
border: 1px solid !important;
border-top-color: #7C836E !important;
border-right-color: #585E51 !important;
border-bottom-color: #3E4239 !important;
border-left-color: #585E51 !important;
color: #000 !important;
text-shadow: 1px 0px 0px #000, 1px 1px 1px #8FA17C !important;
font-weight: normal !important;
font-family: "Segoe UI", Tahoma, sans-serif !important; }

dialog > tabbox > tabs > tab:hover 
{ -moz-appearance: none !important;
background: radial-gradient(at top, #DBEAC7, transparent), linear-gradient(to bottom,rgba(85,116,132, .6), rgba(150,150,150, .2), transparent) !important;
border: 1px solid !important;
border-top-color: #9EA891 !important;
border-right-color: #7B8371 !important;
border-bottom-color: #646958 !important;
border-left-color: #7B8371 !important;
color: #D6E5C2 !important;
text-shadow: 1px 2px 1px #2C2F28 !important; }

dialog .tab-text
{ font-weight: normal !important;
color: #000 !important;
text-shadow: none !important; }

dialog tab[selected="true"] .tab-text
{ color: #D7E8C1 !important;
text-shadow: 2px 2px 2px #000, 1px 1px 1px #999 !important; }

.tabs-right, .tabs-left
{ border: none !important; }

.groupbox-title, caption, .checkbox-label
{ font-weight: bold !important;
color: black !important;
text-shadow: 1px 1px 2px #333, 1px 1px 2px #999 !important; }

#navigator-toolbox > toolbar:not(:-moz-lwtheme)
{ -moz-appearance: -moz-win-glass !important; 
background: none !important;
border: none !important; }

label[control], label.radio-label, label.checkbox-label
{ -moz-appearance: none !important;
background: none !important;
color: #000 !important;
text-shadow: 0px 0px 6px #999 !important; }

textbox:not([input])
{ -moz-appearance: none !important; 
background: transparent !important; }

textbox, groupbox description 
{ -moz-appearance: none !important; 
color: black !important;
text-shadow: 1px 0px 0px, 0px 0px 4px white !important; }

.textbox-input-box.numberbox-input-box, #inspector-inspect-toolbutton
{ color: #BED4AB !important;
text-shadow: 1px 1px 2px #000, 1px 1px 2px #000 !important; }

treecol, treecols, treecolpicker 
{ -moz-appearance: none !important; 
border: 1px solid rgba(0,0,0, .3)  !important;
color: black !important;
text-shadow: 0px 2px 4px #777 !important;  		
background: rgba(175,175,175, .1) !important; }

treechildren::-moz-tree-separator
{ border: none !important;
background-color: black !important; } 

/* ====== buttons and dialog tabs */

groupbox, #greasemonkey #itemBox > vbox:first-child
{ -moz-appearance: -moz-win-glass !important; 
border: none !important;
box-shadow: none !important;
background: none !important; }

.dialog-button, dialog button, .wizard-button, 
#downloadManager #clearListButton, 
tab #palette, dialog textbox, 
#greasemonkey #itemBox,
#print-preview-toolbar button, notification > button
{ -moz-appearance: none !important;
border-radius: 3px !important;
background: radial-gradient(at top, #A0AA8E, transparent), linear-gradient(to bottom,rgba(150,150,150, .6), rgba(150,150,150, .2), transparent) !important;
border: 1px solid !important;
border-top-color: #7C836E !important;
border-right-color: #585E51 !important;
border-bottom-color: #3E4239 !important;
border-left-color: #585E51 !important;
color: #000 !important;
text-shadow: 1px 0px 0px #000, 1px 1px 1px #8FA17C !important;
font-weight: normal !important;
font-family: "Segoe UI", Tahoma, sans-serif !important; }

.dialog-button:hover, button:not(.landingButton):hover, .wizard-button:hover, 
#downloadManager #clearListButton:hover, 
#openHandler, #greasemonkey #scriptDescription,
#print-preview-toolbar button:hover,
.toolbar-portrait-page.tabbable:hover,
.toolbar-landscape-page.tabbable:hover,
notification > button:hover
{ -moz-appearance: none !important;
background: radial-gradient(at top, #DBEAC7, transparent), linear-gradient(to bottom,rgba(85,116,132, .6), rgba(150,150,150, .2), transparent) !important;
border: 1px solid !important;
border-top-color: #9EA891 !important;
border-right-color: #7B8371 !important;
border-bottom-color: #646958 !important;
border-left-color: #7B8371 !important;
color: #D6E5C2 !important;
text-shadow: 1px 2px 1px #2C2F28, 1px 2px 1px #000 !important; }

#print-preview-toolbar button,
.toolbar-portrait-page.tabbable,
.toolbar-landscape-page.tabbable, 
#print-preview-toolbar #scale
{ color: #BED4AB !important;
text-shadow: 1px 2px 1px #000, 1px 2px 1px #000 !important; }

.toolbar-portrait-page.tabbable,
.toolbar-landscape-page.tabbable, 
#print-preview-toolbar #scale menulist
{ margin: 0 2px !important;
padding: 2px !important;
border: 1px solid transparent !important; }

.toolbar-portrait-page.tabbable image,
.toolbar-landscape-page.tabbable image
{ display: none !important; }

toolbarbutton[disabled="true"]
{ color: #333 !important;
text-shadow: none !important;
font-weight: normal !important; }

#clearListButton
{ -moz-appearance: none !important; 
color: #000 !important;
text-shadow: 1px 1px 2px #222, 1px 1px 2px #999 !important;
opacity: .8 !important; }

#clearListButton:hover
{ opacity: 1 !important; }

/* from Paenglab - MoZine, thanks a mill! makes my scrollbar transparent again! */
#appcontent:not(:-moz-lwtheme)
{ background-color: transparent !important;
/* this is personal, comment if you don't need it > */ padding-right: 4px !important; }

toolbarbutton, .toolbarbutton-1, toolbar .toolbarbutton-1,
.toolbarbutton-1 > .toolbarbutton-menubutton-button,
.toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker
{ -moz-appearance: none !important;
border: none !important; 
box-shadow: none !important; 
background: transparent !important; }

toolbar[iconsize="small"] .toolbarbutton-1 > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
toolbar[iconsize="small"] .toolbarbutton-1 > .toolbarbutton-icon, toolbarbutton icon
{ min-width: 16px !important;
min-height: 16px !important; }

