@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
.new_post_label {
  padding: 71px 1px 8px 0 !important;
  text-transform: lowercase;
}

.new_post_label{font-size:0px !important;}

.new_post_label > I {
  background-image: url('http://i.imgur.com/MvASNuR.png') !important;
  background-size: auto !important;
  transition-property: background-position !important;
}

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}


#new_post_label_text > I {background-position: 2px -5px !important}
