css:
/*
name:       deviantP0LLS v7 (rotane)
author:     rotane.deviantart.com
homepage:   http://userstyles.org/styles/88428/
homepage:   http://fav.me/d67dkia
created:    June 1, 2013
updates:    June 24, 2014, v1.1.5
short info: Makes poll bars slim and grey! (Applies to polls, donation pools, and pageview stats.)
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* global (user page, MC, donations, pageview stats) */
#deviantART-v7 .ctube,
#deviantART-v7 #deviant table.layout td div.poll div.result { 
 margin-bottom: 1px !important; 
 padding: 1px 0 0 1px !important; 
 height: 12px !important; 
 border-radius: 6px !important; 
 background: #849889 !important; 
 background: linear-gradient(to bottom, #849889, #A8BCAD) !important;
 box-shadow: inset 0 1px 0 rgba(0,0,0,0.15), 0 1px 0 rgba(255,255,255,0.75) !important; 
}

#deviantART-v7 .ctube:not(:first-child) { margin-top: 9px !important; }

#deviantART-v7 .cfill,
#deviantART-v7 #deviant table.layout td div.poll div.bar { 
 height: 11px !important; 
 border-radius: 5px !important; 
 background: #F5FAF4 !important; 
 background: linear-gradient(to bottom, #F5FAF4, #D3DDD2) !important; 
 box-shadow: inset 0 1px 0 rgba(255,255,255,0.95), 0 -1px 0 rgba(255,255,255,0.15) !important;
}
#deviantART-v7 .cfill[style="width: 100%"],
#deviantART-v7 .cfill[style="width: 100%;"] { width: 398px !important; }

#deviantART-v7 .ctube span { display: block !important; top: -7px !important; padding: 0 1px 0 4px !important; font-size: 9px !important; color: #626C5F !important; font-weight: bold !important; }
/* #deviantART-v7 #output > div > div:not(#messages) .ctube span.sub20 { color: #FAFFF9 !important; text-shadow: 0 1px 0 rgba(0,0,0,0.3) !important; } */
#deviantART-v7 #output .ctube span.sub20 { color: #FAFFF9 !important; text-shadow: 0 1px 0 rgba(0,0,0,0.3) !important; }


/* MC fixes */
#deviantART-v7 #messages .ctube .cfill span { line-height: 10px !important; top: 0 !important; }
#deviantART-v7 #messages .ctube .cfill[style="width: 100%"],
#deviantART-v7 #messages .ctube .cfill[style="width: 100%;"] { width: 349px !important; }


/* pageview stats fixes */
#deviantART-v7 #deviant table.layout { width: 80% !important; margin: 0 auto !important; }
#deviantART-v7 #deviant table.layout td div.poll div.result strong { display: block !important; font-size: 9px !important; top: 0px !important; margin-left: -10px !important; color: #FAFFF9 !important; text-shadow: 0 1px 0 rgba(0,0,0,0.3) !important; padding: 0 !important; font-weight: bold !important; }



/* in footer */
#deviantART-v7 .poll-bar2 { 
 margin-bottom: 2px !important; 
 padding: 1px !important; 
 height: 12px !important; 
 border-radius: 6px !important; 
 background: #324339 !important; 
 background: linear-gradient(to bottom, #324339, #48594F) !important;
 box-shadow: inset 0 1px 0 rgba(0,0,0,0.33), 0 1px 0 rgba(255,255,255,0.25) !important; 
}

#deviantART-v7 .poll-bar2:not(:first-child) { margin-top: 9px !important; }

#deviantART-v7 .poll-bar2 .on { 
 border-radius: 5px !important; 
 background: #819284 !important; 
 background: linear-gradient(to bottom, #819284, #657668) !important; 
 box-shadow: 1px 1px 2px rgba(0,0,0,0.2), inset 0 1px 0 rgba(255,255,255,0.2), 0 -1px 0 rgba(255,255,255,0.05) !important;
}

#deviantART-v7 .poll-bar2 .on[style="width:4%;"] { height: 11px !important; margin-bottom: 0px !important; border-radius: 4px 3px 3px 4px !important;}
#deviantART-v7 .poll-bar2 .on[style="width:3%;"] { height: 11px !important; margin-bottom: 0px !important; border-radius: 4px 2px 2px 4px !important;}
#deviantART-v7 .poll-bar2 .on[style="width:2%;"] { height:  9px !important; margin-bottom: 1px !important; border-radius: 2px 2px 2px 2px !important;}
#deviantART-v7 .poll-bar2 .on[style="width:1%;"] { height:  7px !important; margin-bottom: 2px !important; border-radius: 2px 0px 0px 2px !important;}

#deviantART-v7 .poll-bar2 span { margin-top: 0px !important; padding: 0 1px 0 1px !important; color: #B0B3AD !important; font-size: 9px !important; font-weight: bold !important; text-shadow: 0 1px 0 rgba(0,0,0,0.3) !important; }
#deviantART-v7 .poll-bar2 .on span { margin-top: -1px !important; color: #D5DAD5 !important; }



}
