css:
@-moz-document url-prefix("feedly.com") {
#feedlyFrame { width : 98% !important;}
#feedlyPart { width : 98% !important;}
#feedlyPage { width: 100% !important;} 
#mainArea { width: 100% !important;}
#sideArea { margin:0 15px 0 0; position:absolute; right:0; top:50px}

.entryHolder {
width:100% !important;
max-width: 100% !important;
}

.content img { margin-left:0px !important  }

.u100Entry { width: 100% !important; }
.u100Entry.selectedEntry {
width: none !important; 
 width: 100% !important; 
 }

.topRecommendedEntry{width: 315px!important;}

.topRecommendedEntry .summary {margin-top:8px;}

.topRecommendedEntry .metadata {padding-top:5px; margin-top:7px; border-top:1px #bbb dotted}

.entryHeader .metadata {margin-bottom:8px!important;}

.content{max-width:800px;}

.content img{margin-left:0px!important}

.feedIndex{width:185px;}

.u100frame{ padding:0 0 0 0 !important; }

.u100frame table{ padding:10px !important; }

.u100Entry iframe { margin-left: 0 !important; }

.area { margin-left:0 !important } 

.u100Entry .title { margin-bottom:5px !important }

.entryBody, #messageBarContent, .dot{
font-family:"Lucida Sans Unicode", "Lucida Grande", sans-serif!important}

.entryList .u4Entry,
#mainArea .u10Entry 
 { max-width:80% !important }

#floatingBar{ width: 73% !important}

#feedlyTabsHolder{ margin-top:10px !important; height:100% !important; }

#feedlyTabs { height:100% !important; }
}
