@-moz-document url-prefix("https://www.facebook"), url-prefix("http://www.facebook"), url-prefix("https://apps.facebook"), url-prefix("http://apps.facebook") {
/*Optional*/


/* Hidden left column*/
#pagelet_welcome_box, #pagelet_navigation, #pagelet_friends_online {
display: none !important;
}

#leftCol:hover #pagelet_welcome_box, #leftCol:hover #pagelet_navigation, #leftCol:hover #pagelet_friends_online {
display: block !important;
}

#leftCol {
min-height: 700px !important;
}


/*Scrollbar*/
::-webkit-scrollbar {
width: 12px;
background: #fff;
}
 
::-webkit-scrollbar-track {
-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
border-radius: 10px;
}
 
::-webkit-scrollbar-thumb {
border-radius: 10px;
-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5);
background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#311), color-stop(50%,#811), color-stop(100%,#311));
}

::-webkit-scrollbar-button {
background: #fff;
}

::-webkit-scrollbar-track-piece {
background: #fff;
}


/*Messenger*/
._51jx, ._51jw{
  background-image: none !important;
background-color: #f03d25 !important;
border: 1px solid #d83722 !important;
border-bottom: 1px solid #c0311e !important;
border-top: 1px solid #e23923 !important;
-webkit-border-radius: 2px;
-webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, .77);
display: block;
    width:auto !important;
    height:auto !important;
  padding:0 1px !important; 
}

._50mz .metaInfoContainer {
background-color: transparent !important;
padding-left: 0 !important;
position: absolute;
right: none !important;
top: none !important;
bottom: 4px !important;
}

.messages:hover .metaInfoContainer {
visibility: visible !important;
}

.fbChatConvItem:hover .metaInfoContainer {
visibility: hidden;
}

.fbChatConvItem .profileLink .profilePhoto {
border-radius: 300px !important;
}

.fbChatConvItem .profileLink:before {
background:transparent;
display: block;
width: 32px;
height: 32px;
content: "";
position: absolute;
box-shadow: 0 1px 4px -1px #000, 0 2px 4px -2px #000, inset 0 0px 0px 1px rgba(255, 255, 255, 0.25), inset 0 4px 5px -1px rgba(255, 255, 255, 0.25);
