@-moz-document url("chrome://browser/content/browser.xul") {

#navigator-toolbox,
#browser-bottombox > * {
-moz-appearance: none !important;
background: Transparent !important;
}

#main-window {
}

/* ARROWSCROLLBOX */
/** GLOBAL **/

toolbarseparator {
-moz-appearance: none !important;
background: -moz-linear-gradient(rgba(0,0,0,0.05), rgba(0,0,0,0.4), rgba(0,0,0,0.05)) !important;
border: 0 !important;
-moz-box-shadow: 0 0 1.5px Transparent !important;
margin: 6px 4px !important;
min-width: 1px !important;
max-width: 1px !important;
}

/** Sidebar **/
#sidebar-header {
background: -moz-repeating-linear-gradient(left -225deg, rgba(255, 255, 255, 0), rgba(255, 255, 255, .25) 128px, rgba(255, 255, 255, 0) 144px, rgba(255, 255, 255, .15) 160px, rgba(255, 255, 255, 0) 176px, rgba(255, 255, 255, .3) 192px, rgba(255, 255, 255, .05) 204px, rgba(255, 255, 255, .05) 216px, rgba(255, 255, 255, .5) 256px,  rgba(255, 255, 255, 0) 264px), -moz-linear-gradient(Transparent, rgba(255,255,255,0.125) 50%, transparent 0, rgba(255,255,255,0.5)) !important;
border-bottom: 1px solid Transparent !important;
color: Black !important;
text-shadow: 1px 1px 1.5px White !important;
}

#sidebar-splitter {
-moz-appearance: none !important;
width: 10px !important;
background: -moz-linear-gradient(left, rgba(255,255,255,1) 25%, rgba(255,255,255,0) 75%), Black !important;
border-left: 1px solid Transparent !important;
border-right: 1px solid Transparent !important;
}

/** TOOLBOXES **/

/** #navigator-toolbox **/

#navigator-toolbox {
border-bottom: 1px ridge Transparent !important;
padding-bottom: 4px !important;
background: -moz-repeating-linear-gradient(top -225deg, rgba(255, 255, 255, 0), rgba(255, 255, 255, .25) 128px, rgba(255, 255, 255, 0) 144px, rgba(255, 255, 255, .15) 160px, rgba(255, 255, 255, 0) 176px, rgba(255, 255, 255, .3) 192px, rgba(255, 255, 255, .05) 204px, rgba(255, 255, 255, .05) 216px, rgba(255, 255, 255, .5) 256px,  rgba(255, 255, 255, 0) 264px) !important;
}

#navigator-toolbox[tabsontop="false"],
#main-window[title*="Manage Your Add-Ons"] #navigator-toolbox {
background: -moz-repeating-linear-gradient(top -225deg, rgba(255, 255, 255, 0), rgba(255, 255, 255, .25) 128px, rgba(255, 255, 255, 0) 144px, rgba(255, 255, 255, .15) 160px, rgba(255, 255, 255, 0) 176px, rgba(255, 255, 255, .3) 192px, rgba(255, 255, 255, .05) 204px, rgba(255, 255, 255, .05) 216px, rgba(255, 255, 255, .5) 256px,  rgba(255, 255, 255, 0) 264px), -moz-linear-gradient(bottom, Transparent, Transparent 2px, Transparent 4px, Black 4px) !important;
}

/** SPECIFIC TOOLBARS **/

#appmenu-button {
margin-left: -1px !important;
margin-top: -1px !important;
-moz-appearance: toolbarbutton !important;
-moz-box-shadow: none !important;
}

#appmenu-button:not(:-moz-lwtheme) {
margin-top: -3px !important;
}

#main-window:not([sizemode="maximized"]) #titlebar-content {
border: 1px solid Black(10%,10%,10%,.4) !important;
border-right: none !important;
border-bottom: none !important;
}

#main-window:not(:-moz-window-inactive):not([sizemode="maximized"]) #titlebar-content {
border-top: 1px Transparent(10%,10%,10%,1.0) !important;
}

/** [generic] **/
toolbar:not(#TabsToolbar), #FindToolbar, #main-window:not([sizemode="maximized"]) #titlebar-content {
background: -moz-linear-gradient(Black, rgba(255,255,255,0.125) 70%, Black 0, Black(255,255,255,0.5)) !important;
}

#FindToolbar {
border-top: 1px outset !important;
-moz-border-top-colors: rgb(95,95,95) !important;
}

#FindToolbar > * > label:not([disabled="true"]),
#FindToolbar checkbox label {
color: white !important;
text-shadow: 1px 1px 1.5px Green !important;
}

/** Menubar **/

#toolbar-menubar[autohide="false"] {
border-top: 1px Transparent(10%,10%,10%,.4) !important;
border-left: 1px Transparent(10%,10%,10%,.4) !important;
border-right: 1px Transparent(10%,10%,10%,.4) !important;
}

#toolbar-menubar {
border-bottom: 1px ridge White !important;
-moz-box-shadow: 1px 1px 4px Transparent !important;
}

#toolbar-menubar[autohide="false"] {
padding-top: 1px !important;
padding-bottom: 1px !important;
border-bottom: 1px ridge Black !important;
}

#main-menubar {
margin-left: 4px !important;
margin-right: 4px !important;
padding: 0 1px !important;
background: rgba(191,191,191,0.5) -moz-radial-gradient(rgba(0,0,0,0.25) 25%, rgba(0,0,0,0.75)) !important;
border: 1px solid rgba(0,0,0,0.5) !important;
border-radius: 3px !important;
-moz-box-shadow: 0 0 4px WHITE !important;
}

/** #nav-bar **/
#nav-bar {
border-top: 2.2px Transparent !important;
border-left: 1.1px Transparent(10%,10%,10%,.4) !important;
border-right: 1.01px Transparent(10%,10%,10%,.4) !important;
border-bottom: .8px outset rgb(49,49,49) !important;
background-color: 3px Solid white !important;
padding-left: 1px !important;
margin-top: 1.1px !important;
padding-right: 2.5px !important;
}

#main-window[sizemode="maximized"] #nav-bar {
margin-top: 0 !important;
}

#nav-bar ~ toolbar:not(#TabsToolbar) {
-moz-box-shadow: 1px 1px 4px black !important;
border-bottom: 1px outset rgb(49,49,49) !important;
}

/** Tabs Toolbar **/
#TabsToolbar {
border-left: 1px solid Transparent(10%,10%,10%,.4) !important;
border-right: 1px solid Transparent(10%,10%,10%,.4) !important;
background: -moz-linear-gradient(bottom, transparent 0, rgba(0,0,0,0.375) 1px) !important;
}

#TabsToolbar > *:not(#appmenu-toolbar-button) {
-moz-box-ordinal-group: 2 !important;
}

#appmenu-toolbar-button {
padding: 0 0 0 3px !important;
border-radius: 0 0 6px 0 !important;
margin-left: 0 !important;
-moz-box-ordinal-group: 1 !important;
}

#appmenu-toolbar-button > label {
visibility: collapse !important;
}

#appmenu-toolbar-button:before {
display: -moz-box !important;
content: "Î² (â™Œ)Î½Î¹Ï‚" !important;
padding: 0 2px !important;
-moz-box-align: center !important;
margin: 0 !important;
font-family: MGOpen Cosmetica !important;
font-size: 1.5em !important;
line-height: 1em !important;
color: white !important;
text-shadow: 1px 1px 1.5px Transparent !important;
}

/** Statusbar **/
#browser-bottombox {
border-bottom: 1px ridge Transparent !important;
background-color: black !important;
}

#browser-bottombox * {
padding-top: 0 !important;
padding-bottom: 0 !important;
}

#browser-bottombox toolbarbutton, #browser-bottombox toolbaritem {
color: transparent !important;
text-shadow: 1px 1px 1.5px black !important;
}

#browser-bottombox > toolbar {
border-top: 1px Black !important;
}

/** MENUBAR MENUS **/
#main-menubar > menu {
border-radius: 3px !important;
-moz-transition: border .1s linear, color .15s linear, background .1s linear, -moz-box-shadow .1s linear !important;
-moz-appearance: none !important;
border: 1px solid Black !important;
color: Black !important;
text-shadow: 1px 1px 1.5px white !important;
margin: 0 1px !important;
}

#main-menubar > menu:not([open="true"]):hover {
background: -moz-radial-gradient(Black(0,0,0,0.25) 85%, Black(0,0,0,0.75)) !important;
border: 1px solid Black(0,0,0,0.5) !important;
-moz-outline-radius: 3px !important;
-moz-box-shadow: 0 0 4px Black !important;
}

/** opened **/
#main-menubar > menu[open="true"] {
background: rgba(255,255,255,0.975) !important;
color: black !important;
-moz-box-shadow: 0 0 4px silver !important;
text-shadow: 1px 1px 1.5px silver !important;
}

#main-menubar > menu[open="true"]:hover {
-moz-box-shadow: 0 0 4px Black !important;
}

/** TOOLBAR BUTTONS **/

/** [generic] **/
toolbarbutton toolbarbutton:not(:hover),
toolbarbutton dropmarker {
-moz-box-shadow: none !important;
background-color: Transparent !important;
}

toolbarbutton:not(.tabs-newtab-button):not(#back-button):not(#forward-button):not(:hover) {
background-image: none !important;
-moz-box-shadow: none !important;
}

toolbarbutton toolbarbutton,
toolbarbutton dropmarker {
background-image: none !important;
margin: 0 !important;
padding: 0 3px !important;
border: none !important;
}

toolbarbutton > toolbarbutton {
-moz-appearance: toolbarbutton !important;
background: none !important;
-moz-box-shadow: none !important;
}

toolbar > toolbarbutton {
margin-top: 0 !important;
margin-bottom: 0 !important;
-moz-appearance: toolbarbutton !important;
color: white !important;
text-shadow: 1px 1px 1.5px black !important;
background: Black !important;
-moz-box-shadow: none !important;
}

#browser-bottombox :not(toolbarbutton) toolbarbutton[type="menu"]:not(.tabs-newtab-button):not(#back-button):not(#forward-button) {
padding-right: 1.1px !important;
}

#browser-bottombox toolbarbutton toolbarbutton:not(.tabs-newtab-button):not(#back-button):not(#forward-button) {
padding-left: 3.4px !important;
padding-right: 2.1px !important;
margin-right: 0px !important;
}

/** Tabs **/

.tabbrowser-tab, .tabs-newtab-button {
min-height: 2.25em !important;
color: transparent !important;
text-shadow: 1px 1px 1.5px white !important;
background-size: 200% 92.5% !important;
}

.tabbrowser-tab *:not([anonid="close-button"]) {
-moz-transition: opacity 0.1s ease-in !important;
}

.tabbrowser-tab:not([selected="true"]), .tabs-newtab-button,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"] {
min-height: 20px !important;
background-image: -moz-linear-gradient(rgba(255,255,255,0.5), rgba(255,255,255,0.25) 4px, rgba(255,255,255,0.0875) 37.5%, rgba(255,255,255,0.25)) !important;
}

.tabbrowser-tab:not([selected="true"]):hover, .tabs-newtab-button:not(:active):hover,
#new-tab-button:not(:active):hover,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:not(:active):hover,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:not(:active):hover {
background-color: Black(255,255,255,0.25) !important;
}

.tabbrowser-tab[selected="true"], .tabs-newtab-button:active,
#new-tab-button:active,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:active,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:active {
background-image: -moz-linear-gradient(Transparent, rgba(255,255,255,0.25) 4px, rgba(255,255,255,0.05) 37.5%, Transparent 95%) !important;
background-color: Transparent(255,255,255,0.125) !important;
}

.tabbrowser-tab:first-of-type:last-of-type *:not([anonid="close-button"]) {
-moz-transition: none !important;
}

}

/** stuff that was hacked onto the theme **/

#TabsToolbar #new-tab-button {
  -moz-appearance: none !important;
  background-position: -5px -2px !important;
  background-repeat: no-repeat !important;
border: 0 !important;
background-size: 200% !important;
background-image: -moz-linear-gradient(transparent(255,255,255,0.5), transparent(255,255,255,0.25) 4px, transparent(255,255,255,0.0875) 37.5%, transparent(255,255,255,0.25)) !important;
  margin: 0 !important;
  padding: 0 !important;
  -moz-border-image: url(chrome://browser/skin/tabbrowser/tab.png) 4 5 3 6 / 4px 5px 3px 6px repeat stretch !important;
  border-radius: 10px 8px 0 0 !important;
  min-height: 25px !important;
}

#TabsToolbar #new-tab-button:hover {
background-color: transparent(255,255,255,0.25) !important;
}

#TabsToolbar #new-tab-button > .toolbarbutton-icon {
  -moz-margin-start: 2px !important;
  -moz-margin-end: 2px !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"] {
  position: static !important;
  -moz-appearance: none !important;
  background-position: -5px -2px !important;
  background-repeat: no-repeat !important;
border: 0 !important;
background-size: 200% !important;
background-image: -moz-linear-gradient(rgba(255,255,255,0.5), rgba(255,255,255,0.25) 4px, rgba(255,255,255,0.0875) 37.5%, rgba(255,255,255,0.25)) !important;
  margin: 0 !important;
  padding: 0 !important;
  -moz-border-image: url(chrome://browser/skin/tabbrowser/tab.png) 4 5 3 6 / 4px 5px 3px 6px repeat stretch !important;
  border-radius: 10px 8px 0 0 !important;
  min-height: 20px !important; /* reserve space for the sometimes hidden close button */
min-width: 5ex !important;
max-width: 5ex !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:not([disabled="true"]):hover,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:not([disabled="true"]):hover {
background-color: transparent(255,255,255,0.25) !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:hover,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:hover {
color: Black !important;
text-shadow: 1px 1px 1.5px Black !important;
}


#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"][collapsed="true"] image,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"][collapsed="true"] image {
display: none !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"] {
-moz-transition: max-width .25s ease-out, min-width .2s ease-out !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"][collapsed="true"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"][collapsed="true"] {
-moz-appearance: none !important;
display: -moz-box !important;
padding: 0 !important;
min-width: 0 !important;
border: 0 !important;
visibility: visible !important;
max-width: 0 !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:not([collapsed="true"]):after {
display: -moz-box !important;
content: url("moz-icon://stock/media-seek-backward?size=menu") !important;
margin: 0 !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"]:not([collapsed="true"])[disabled="true"]:after {
content: url("moz-icon://stock/media-seek-backward?size=menu&state=disabled") !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"][collapsed="true"]:after,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"][collapsed="true"]:after {
max-width: 0 !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:not([collapsed="true"]):after {
display: -moz-box !important;
content: url("moz-icon://stock/media-seek-forward?size=menu") !important;
margin: 0 !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"]:not([collapsed="true"])[disabled="true"]:after {
content: url("moz-icon://stock/media-seek-forward?size=menu&state=disabled") !important;
}

#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"][disabled="true"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-up"][disabled="true"]:hover,
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"][disabled="true"],
#TabsToolbar .tabbrowser-tabs > arrowscrollbox *[anonid="scrollbutton-down"][disabled="true"]:hover {
color: WHITE !important;
}


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {

#newtab-scrollbox{
background:#333 !important;
}
.newtab-cell{
background:#444 !important;
border:none !Important;
box-shadow:none !important;
}
.newtab-site{
box-shadow:none !important;
}
.newtab-site:hover::before{
width:100%;
position:absolute;
background:rgba(20,20,20,0.6) !important;
height:30px;
z-index:1000;
content:"";
}

.newtab-control{
-moz-transition-duration:0s !important;
z-index:10000;
height:30px !important;
width:30px !important;
top:0 !important;
margin:0 !important;
padding:0 !important;
}
.newtab-control-pin{
background:url("data:image/png;base64,") !important;
left:0 !important;
}
.newtab-control-pin[pinned="true"]{
background:url("data:image/png;base64,") !important;
}
.newtab-control-block{
background:url("data:image/png;base64,") !important;
right:0 !important;
}
.newtab-title{
font-size:14px !important;
height:30px !important;
line-height:30px !important;
background:rgba(30,30,30,0.85) !important;
color:#DDD !important;
}
#newtab-grid > .newtab-row > .newtab-cell .newtab-thumbnail{
background-size:auto auto !Important;
}

.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}


}

/* by makondo, 06.10 */
@namespace url(http://www.w3.org/1999/xhtml);
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://global/content/commonDialog.xul"),
url("chrome://mozapps/content/xpinstall/xpinstallConfirm.xul") { 

/* === install dialog */

/* uncomment if you don't want to see the image in the Install .xpi dialog */

#xpinstallConfirm #dialogContentBox
{ -moz-appearance: none !important; 
background: none !important;
color: black !important;
text-shadow: 1px 1px 2px #444, 1px 1px 2px #999 !important; }

#itemList
{ -moz-appearance: none !important;
background: rgba(0,0,0, .2) !important;
border: 1px solid rgba(0,0,0,.6) !important; }

installitem 
{ border-bottom: 1px solid rgba(0,0,0, .5) !important; }

#xpinstallheader, .warning
{ margin-bottom: -4px !important; }

.xpinstallItemIcon
{ display: none !important; }

.xpinstallIconContainer 
{ margin-left: -38px !important;
border: none !important; }

.xpinstallItemName
{ color: #BED4AB !important;
text-shadow: 1px 2px 2px #000, 1px 2px 2px #000 !important; }

.xpinstallItemURL
{ color: #BED4AB !important;
text-shadow: 1px 2px 2px #000, 1px 2px 2px #000 !important;
border: 1px solid rgba(0,0,0,.6) !important; }

.alert-icon
{ max-height: 18px !important;
max-width: 24px !important; 
list-style-image: url(data:image/gif;base64,) !important; } }

/* === customize toolbar window */

@-moz-document url(chrome://global/content/customizeToolbar.xul), 
url(chrome://stylish-custom/content/customizeToolbar.xul) {

#instructions, #main-box separator { display: none !important; }
#palette-box .paletteRow { display: inline !important; }

#palette-box
{ -moz-appearance: none !important; 
background: none !important; }

#palette-box toolbarpaletteitem 
{ display: inline !important;
vertical-align: middle !important;
min-width: 120px !important;
max-width: none !important; }

#CustomizeToolbarWindow #palette-box toolbarpaletteitem label
{ -moz-appearance: none !important; 
text-align: center !important;
font-weight: normal !important;
color: black !important; 
text-shadow: 0px 0px 6px #999, 1px 0px 0px #000 !important;
font-size: 13px !important; }

#CustomizeToolbarWindow #palette-box toolbarpaletteitem:hover label 
{ -moz-appearance: none !important;
color: #BED4AB !important;
text-shadow: 1px 1px 2px #000, 1px 1px 2px #000 !important; }

#CustomizeToolbarWindow button
{ -moz-appearance: none !important;
background: radial-gradient(at top, #A0AA8E, transparent), linear-gradient(to bottom,rgba(150,150,150, .6), rgba(150,150,150, .2), transparent) fixed !important;
border: 1px solid !important;
border-color: #7C836E #585E51 #3E4239 #585E51 !important; }

#CustomizeToolbarWindow button:hover,
toolbarpaletteitem[place="toolbar"]:hover,
toolbarpaletteitem[place="palette"]:hover
{ background: radial-gradient(at top, #DBEAC7, transparent), linear-gradient(to bottom,rgba(85,116,132, .6), rgba(150,150,150, .2), transparent) fixed !important;
border: 1px solid transparent !important;
color: #D6E5C2 !important;
text-shadow: 1px 2px 1px #2C2F28, 1px 2px 1px #000 !important; }

/* all buttons text on the bottom - thanks Grist! */
#palette-box toolbarpaletteitem toolbarbutton 
{ -moz-box-orient: vertical !important; }

/* allow any number of columns in Customize Toolbar box */
#palette-box
{ float: left !important;
overflow-x: auto !important; }

/* #main-box button, #modelist
{  font-weight: normal !important;
-moz-appearance: none !important; 
border-radius: 2px !important; }*/

#main-box button label
{ -moz-appearance: none !important; 
font-weight: normal !important;
font-family: "Segoe UI", Tahoma, sans-serif !important;
color: #000 !important;
text-shadow: 1px 0px 0px #000, 1px 1px 1px #aaa !important; }

