css:
@-moz-document url-prefix('http://www.wamap.org/'), url-prefix('https://www.wamap.org/') {

    html, body, div.navbar {
        color: white !important;
        background-color: #2b2b2b !important;
    }
    body {
        margin-top: 5px !important;
    }

    /* Force white text */
    *[style="color: #000000;"] {
        color: white !important;
    }

    img {
        background-color: white !important;
        padding: 1px;
        border: 1px solid black !important;
    }
    
    a:link, a:visited {
        color: #0094ff !important;
        text-shadow: 0 1px 0 black !important;
    }
    
    a:hover {
        color: #3070ff !important;
    }

    font[color='blue'], span[style='color: #0000ff;'], span[style='color: blue;'] {
        color: #5070ff !important;
    }
    
    input, a.abutton, button, .mceActionPanel > input {
        -moz-appearance: none !important;
        background: none #505050 !important;
        border: 1px solid #101010 !important;
        color: white !important;
    }
    input[type='submit']:hover, .abutton:hover, button:hover, .mceActionPanel input:hover {
        background-color: #606060 !important;
        color: white !important;
        cursor: pointer !important;
    }

    /* Dark bordered box */
    .navbar, .breadcrumb, .homefullwidth, .block, .blockitems, .item,
        #navlist, #navlist li > a, .cpmid, #loginbox, table.stats th {
        background-color: inherit !important;
        border: 1px solid black !important;
    }
    /* Medium bordered box */
    #leftcontent, #centercontent, table.cal, .cal td, table.stats, table.stats th[scope="row"], #MathJax_Zoom {
        background-color: #3c3c3c !important;
        border: 1px solid black !important;
    }
    /* Dark unbordered box */
    .intro, .text > *, #loginbox td {
        background-color: #2b2b2b !important;
        border: none !important;
    }

    /* Column-specific tweaks (#leftcontent, #centercontent) */
    div#leftcontent {
        position: static !important;
        float: left;
        margin-top: 0 !important;
    }

    div#centercontent {
        padding: 0 10px 0 10px !important;
        margin-top: 0 !important;
        margin-left: 190px !important;
    }

    /* Navlist stuff */

    .headerwrapper, #navlist {
        background-color: black !important;
        border-left: none !important;
        border-right: none !important;
    }
    #navlist > li > a {
        padding-bottom: 3px !important;
        border: none !important;
        border-bottom: 1px solid transparent !important;
        color: white !important;
        text-shadow: 0 1px 0 #3070ff !important;
    }
    #navlist a:hover {
        color: #c0c0c0 !important;
        border-bottom-color: #3070ff !important;
    }

    .ddmenu {
        background-color: #101010 !important;
        border-color: black !important;
    }

    .midwrapper > div.breadcrumb {
        padding-top: 5px !important;
        background-color: black !important;
    }

    /* Homepage stuff */
    #header {
        border: 1px solid black !important;
        background: -moz-linear-gradient(15deg, black, white 75%) !important;
    }
    #header img {
        border: none !important;
        padding: 0;
    }

    /* Course homepage: accordion blocks */
    .block img, img[alt='assessment'] {
        background: none transparent !important;
        border: none !important;
    }

    /* Correct double borders */
    div.blockitems {
        border-top: none !important;
    }

    /* Gradebook breakdown table recolor */
    .cat0 { background-color: #433 !important; }
    .cat1 { background-color: #443 !important; }
    .cat2 { background-color: #343 !important; }
    .cat3 { background-color: #344 !important; }
    .cat4 { background-color: #334 !important; }
    .cat5 { background-color: #434 !important; }
    
    /* Calendar recolor */
    a.caldl { background-color: #3c3c3c !important; }
    .cal th, #caleventslist > div:first-child { background-color: #353535 !important; }
    .calitem {
        background-color: #2b2b3b !important;
        border: 1px solid white;
    }
    .cal td.today .day {
        font-weight: bold;
        background-color: transparent !important;
    }
    .cal td.today { background-color: #2d2d3d !important; }
    
    .intro {
        border-color: #059 !important;
    }
    
    table {
        background-color: #454545 !important;
        border: 1px solid black !important;
    }
    
    table th {
        background-color: black !important;
        border-bottom-color: #0094ff !important;
    }
    table td {
        background-color: #454545 !important;
        padding: 1px 4px 1px 4px !important;
        border: none !important;
        border-bottom: 1px solid black !important;
    }
    form#qform table tr > td:nth-child(n + 2) {
        padding-right: 40px !important;
    }
    form#qform table tr > td:nth-child(n + 7) {
        padding-right: 20px !important;
    }
    
    tr.tagged { background-color: #433 !important; }

    /* Remove weird logo in top bar and reposition name */
    div.midwrapper > div#headerlogo {
        display: none !important;
    }
    div.midwrapper > div.breadcrumb > span.padright {
        padding-right: 0 !important;
    }
    
    /* Send message/forum posting formatting */
    div.editor .mceFirst *, .mceIframeContainer { border: none !important; }

    tr.mceFirst, tr.mceFirst .voiceLabel, tr.mceFirst .mceText, .mceMenu * {
        color: black !important;
        text-shadow: none !important;
    }

    .mceStatusbar {
        background-color: #555555 !important;
        border-width: 1px 0 0 0 !important;
    }

    table.mceToolbar * { padding-right: 2px !important; }

    /* mce popup recolor */
    .clearlooks2 .mceMiddle .mceLeft, .clearlooks2 .mceMiddle .mceRight, .clearlooks2 .mceBottom > div {
        background: none gray !important;
    }
    div.tabs li span { background: none transparent !important; }
    div.tabs {
        background-image: none !important;
        border-bottom: 2px solid black !important;
        border-left: 1px solid #2b2b2b !important;
        margin-bottom: -1px !important;
    }
    div.tabs li {
        cursor: pointer;
        background: none #252525 !important;
        border-width: 1px 1px 1px 1px !important;
        border-style: solid;
        border-color: black;
    }
    div.tabs li[aria-selected='true'] {
        cursor: initial;
        background-color: #353535 !important;
        border-bottom: 1px solid #353535 !important;
    }
    div.tabs li a:hover { text-decoration: none !important; }
    div.panel_wrapper {
        background-color: #353535 !important;
        border-color: black !important;
        overflow: auto;
    }
    div.panel_wrapper td { border: none !important; }
    
    .mceActionPanel input {
        border-radius: 4px !important;
    }

    /* Settings */

    img[src='/img/gears.png'] {
        background: none transparent no-repeat !important;
        border: none !important;
    }

    #GB_frameholder, #GB_window {
        background-color: #2b2b2b !important;
    }

    #GB_caption {
        background-color: #005080 !important;
    }
    #GB_window {
        border-color: #005080 !important;
    }
}

@-moz-document url-prefix('https://www.wamap.org/assessment/') {
    /* Assessments (Questions) */

    div.navbar {
        background-color: #303636 !important;
        width: 160px !important;
        clear: left !important;
    }
    div.question, div.question div {
        background-color: white !important;
        color: black !important;
    }
    div.question table * {
        background-color: #e0e0e0 !important;
        border-color: #c0c0d0 !important;
    }
    div.question img {
        background-color: transparent !important;
        border: none !important;
    }

    div.question input.text { background-color: #505050 !important }

    div.inset {
        margin-top: 2px !important;
    }

    #headershowtest.pagetitle {
        width: auto !important;
        float: left !important;
    }
    #headershowtest.pagetitle > h2 {
        margin-top: 0 !important;
    }

    /* Add max height to questions list and overflow to a scrollbar */
    div.navbar ul {
        max-height: 603px !important;
        overflow: auto;
    }

    ul.qlist > li img, img[src='/img/flagfilled.gif'], img[src='/img/flagempty.gif'] {
        background: none transparent !Important;
        border: none !important;
    }
    
    ul.qlist > li {
        position: relative;
        transition: left linear 20s, background-color linear 0.2s;
        margin-right: 15px;
    }
    ul.qlist li:hover {
        left: 5px !important;
        background-color: rgba(0,0,0,0.2) !important;
    }
    span.current {
        background-color: rgba(0,0,0,0.2) !important;
        border-radius: 3px;
        padding: 2px 4px 2px 2px;
    }

    .navbar > p:last-child {
        margin-top: 10px !important;
        margin-bottom: 5px !important;
    }

    .review {
        background-color: #404040 !important;
        border-color: black !important;
    }
}

@-moz-document url-prefix('https://www.wamap.org/course/gradebook') {
    /* Gradebook */

    table.gb tr.even {
        background-color: #2f2f2f !important;
    }

    table.gb tr.odd {
        background-color: #3f3f3f !important;
    }

    table.gb tbody tr:hover {
        background-color: #3f3f44 !important;
    }
}
