css:
@namespace url(http://www.w3.org/1999/xhtml);  
@-moz-document domain(gamefaqs.gamespot.com) { 

/*Removes "From The Web"*/
.trc_rbox_container > div:nth-child(1){
    display: none !important;
}

/*Reduces the font of the stickies*/
.board a.sticky { 
    font-size:  13px !important; 
    }

/* Reduces size of the page titles*/
.page-title { 
    font-size: 28px !important; 
    }

/*Reduces the titles (like the "Post New Message")*/
.title {
    font-size: 17px !important;
    }
    
/*Compacts the quotes*/
.board blockquote {
    margin: 0px 0px 0px !important;
    padding: 5px !important;
    }

/*Compacts the topic list*/
.board_status { 
    padding: 5px 0 0 5px   !important; 
    } 

/*Removes the "New" image*/
.content_nav > li.updated:before { 
    background: none !important; 
    }

/*Resizes the posts*/
.board.message .msg_body { 
    font-size: 12px !important; 
    } 
    
/*Resizes the posting info in the boards and restores zebra effect*/
.board.message .author { 
    padding: 10px 15px 10px!important; 
    line-height: 15px !important;
    background: none !important; 
    font-size: 11px !important; 
    } 
    
/*Resizes the usernames in the boards*/
.board.message .name { 
   font-size: 13px !important; 
    text-transform: none !important; 
    line-height: 20px !important; 
    } 

/*Capitalizes the Message Detail and the Quote*/
.board.message .author_data a:link { 
    text-transform: capitalize; 
    }

/*Changes the color of the tags*/
.tag {
    border-radius: 5px !important;
    background: #99AACC !important; 
}
    
/*Removes underlines from userinfo*/
table.board.userinfo a { 
    text-decoration: none !important; 
    }

/* Reduces the size of the post header*/ 
.board.message .msg_infobox { 
 padding: 2px 5px 2px!important; 
}

/* Removes horrible blue box in breadcrumbs*/
div.tinynav { 
    background-color: transparent !important; 
    border: none !important; 
}

/*Changes the size of the breadcrumbs font*/
.tinynav .crumb  { 
    font-size: 14px !important; 
    font-weight: bold !important;
    } 

/* Reduces the size of the extra line (Sticky etc) */
.board.message .msg_below {
 padding: 0px 0px 0px!important; 
}

/*Changes the color from hovering the links in userinfo*/
table.board.userinfo a:hover { 
    color: #DDDDDD !important; 
    }

/*Active messages last post color change to white to match the other titles*/
th.lastpost a:visited { 
    color: #FFFFFF!important;
    }

 /*Active Messages hover color change*/ 
th.lastpost a:hover { 
    color: #DDDDDD !important; 
    }

/*Removes underlines from Last post in Active Messages*/
th.lastpost a { 
    text-decoration: none !important; 
    }

/*Removes "More from gamestop*/
div.pod.pod_gs {
   display: none !important;
    }
    
/* Compact website Footer + no black bar */ 
.footer_network { 
 background: none !important; 
 box-shadow: none !important; 
 border-top: none !important; 
 } 
.footer_site, .footer_network { 
    padding: 0 !important; 
}

/*Compresses the search results*/
table.results td {
    padding: 1px!important;
}

/* Change section box color in html FAQs */ 
div[class="section_box"] { 
    background: #B5B8CB !important; 
    }

/*Removes the weird rectangle around "Search Topics"*/
.searchtopics {
    background: none !important;
}

/*Puts the page list in front of the topic title*/
td[class="topic"] br { 
    display: none !important; 
    }

/*Spaces the page list from the topic title*/
td[class="topic"] span[class="pglist"] { 
    padding-left: 10px !important; 
}


/*Expands the width of the boards*/
/*[[expand]]*/
    
}
   
@-moz-document url-prefix(http://www.gamefaqs.com/boards/user.php) {
    
/*Reduce font from message board user info and reduce the blue area*/ 
.board th { 
    font-size:12px !important; 
    padding: 4px 4px 4px!important; 
    } 
.board td { 
    font-size:12px !important; 
    }
.board th {
    width: 155px !important;
    }
}
