css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ioffer.com") {
html, #footer, #optional-footer, #header:before, body {
background:#000 !important;
}
#header, .item-actions, .popular-items, .subtle-panel, .listing tr,
.checkout-box-title, #payment-methods {
background:#000 !important;
}
#content, .category-breadcrumb a, .share-with-facebook,
 .share-with-twitter, .t_buy_one_more, .body, .checkout-box,
.item-subtle a {
color:#fff !important;
background:#333 !important;
}
.sidebar-container, .sidebar-nested, .purpledank 
, #homepage-popular-searches, .selected, .tb_content ul {
background:#000 !important;
}
.sidebar-container a, .sidebar-nested-title
, .no-items, #homepage-popular-searches a,
#featured-testimonial-quote, .listing tr a, .tb_content ul a,
 label, .receivedItem {
color:#fff !important;
}
#header-status-bar {
background:#000 !important;
}
#header-status-bar a, .buyerEngagedShareBar, .softClear, .offerStatus, 
.buyerEngagedFaceboxForm ul li, .view-all-items {
color:#fff !important;
}
.homepage-ratings-inner-cell {
background:#000 !important;
}
.homepage-ratings-inner-cell a, .user-address, 
.title-description a, .homepage-recent-searches {
color:#fff !important;
}
.homepage-recent-searches a {
color:#fff !important;
background:#000 !important;
padding:4px !important;
}
.striped-divider {
display:none !important;
}
.homepage-categories-top {
color:#fff !important;
background:#000 !important;
}
.homepage-categories-top a, .item-comment a, .item-title a {
color:#fff !important;
}
.subtle-sidebar, .subtle-sidebar a {
background:#000 !important;
}
#sidebar-related, #sidebar-keywords, #sidebar-categories {
background:#000 !important;
}
#sidebar-related a, #sidebar-keywords a, #sidebar-categories a {
color:#fff !important;
}
.sidebar-categories a span, .sidebar-recently-searched a span, .sidebar-sellers a span, 
.sidebar-payments a span, .sidebar-groups a span, .sidebar-features a span, 
.sidebar-keywords a span, .subtle-sidebar-header{
color:#fff !important;
}
#subdomain-homepage .left-panel .sidebar-container .sidebar-categories, .subtle-sidebar-header {
background:#000 !important;
}
#header-bottom-bar, .seller  {
background:#000 !important;
}
#header-bottom-bar a, .ui-autocomplete-input, .item-buyer a, .tab {
color:#fff !important;
}
.dropdown, .dropdown-menu, .recent-purchases {
background:#000 !important;
}
.dropdown a {
background:#000 !important;
}
#header-search .search-widget {
border:5px #000 solid !important;
}
#header-search .custom-select select {
color:#fff !important;
background:#000 !important;
}
#header-search .text-field, .glossy-panel {
background:#333 !important;
}
.item-wrapper, .inner-tab, .header, .headline {
background:#000 !important;
}
.item-title, .content-header, #item-description,
 .header, .headline, .glossy-panel, .glossy-panel a {
color:#fff !important;
}
.account_info, .box, #welcome, .question-tab {
background:#000 !important;
}
#form-title, .box-title, .question-tab, .question-title, .response-content, .question-title, 
.response-content a {
color:#fff !important;
}
#user_id, #user_password, #user_password_confirmation
, #recaptcha_response_field, #user_email, #user_email_confirmation
, #user_first_name, #user_last_name, #password, 
#username_or_email, .tabbed_navigation_tab, .clearfix, .rbtop,
 .rbroundbox, .entry-title, .frame, .placeholder, 
 .buyer-actions-dropdown, #buyer-minimal-layout .main-action:not(#dummy), 
 .navigation, #buyer-minimal-layout .navigation li a:not(#dummy) {
color:#fff !important;
background:#333 !important;
}
a, h1, h2, h3, h4, legend, .formblock p, .box label, 
 .action-title, .action-state {
color:#fff !important;
}
.question-footer, .question-answer, .view-questions-first , 
.glossy-container, .left-td, .buyer-actions, .rounded-box  {
background:#000 !important;
}
.question-footer a, .clearfix a, .cart, .total,
.line-items th, .line-items th a, .form-toggle a,
 .bold, .small, .email_notice a, .italic, .row, 
 .buyer, .online, .time, .comment-span, .item-price, 
 .buyer-engaged-negotiation-status {
color:#fff !important;
}
.confirm-msg, .email_notice {
color:#000 !important;
background:red !important;
}
.error-msg, .panel-content, .panel-content strong, .panel-content a {
color:#FFF !important;
background:red !important;
}
.t_browse_categories, .nested a, 
#search-autocomplete-menu, #search-autocomplete-menu a {
color:#FFF !important;
background:#000 !important;
}
.thanks, .copy, .negotiation-header {
color:#FFF !important;
}
}
