display: none
}

body
{
	background:#9f0000 url(http://i.imgur.com/qdscBV9.jpg);
	color:#ffffff;
	font:normal 11px tahoma,helvetica,arial,sans-serif;
	position:relative;
	padding-bottom:20px;
}
#secondary_navigation
{
	background:#000000 url(http://i.imgur.com/ZeH350n.png) repeat-x 0 0;
	border-bottom:1px solid #000000;
	text-shadow:rgba(0,0,0,0.3) 0 -1px 0;
	overflow:hidden;
	font-weight:700;
	color:#fff;
	line-height:37px;
	clear:both;
}

#secondary_navigation a
{
	color:#fff;
	line-height:37px;
	height:37px;
}

#secondary_navigation #breadcrumb li a
{
	padding-left:12px;
	margin-left:-15px;
	background:url(http://i.imgur.com/dJ43X4A.png) no-repeat 0 0;
	display:block;
	outline:none;
	text-decoration:none;
}

#secondary_navigation #breadcrumb li.first a
{
	margin-left:0;
	background:none;
	padding-left:0;
}

#secondary_navigation #breadcrumb li span
{
	display:block;
	padding-right:17px;
	padding-left:4px;
	background:url(http://i.imgur.com/dJ43X4A.png) no-repeat 100% 0;
}

#secondary_navigation #breadcrumb li>span
{
	padding-left:4px;
	background:none transparent;
	color:#fff;
}

#secondary_navigation #breadcrumb li.first a span
{
	padding-left:12px;
	-webkit-border-top-left-radius:3px;
	-webkit-border-bottom-left-radius:3px;
	-moz-border-radius:3px 0 0 3px;
	border-radius:3px 0 0 3px;
}

#secondary_navigation #breadcrumb li a:hover
{
	background-position:0 -43px;
}

#secondary_navigation #breadcrumb li a:hover span
{
	background-position:100% -43px;
}

#secondary_navigation #breadcrumb li a:active
{
	background-position:0 -86px;
}

#secondary_navigation #breadcrumb li a:active span
{
	background-position:100% -86px;
}
