css:
@-moz-document domain("plus.google.com") {

/* top white bar */
.Dd.gb_gbsh, .Dd.gb_gbsf  {
    top: -102px !important;
}

/* bottom top bar */
.iYjCM.gIdB1 {
  top: 0 !important;
  background-color: #fff;
}
    
/* home button */
.Um8btf {
  /*background-color: #fff !important;*/
  top: 0 !important;
}
    
/* notifications button */    
.Dd.gb_gbsf #gbu {
  top: 102px !important;
}
.Dd.gb_gbsf.gb_gbsh #gbu {
  visibility: visible;
  pointer-events: none;
}
.Dd.gb_gbsf #gbu ol {
  visibility: hidden;
}    
.Dd.gb_gbsf li.gbt.gbtn {
  visibility: visible !important;
  pointer-events: auto;
  top: -12px;
}
.Dd.gb_gbsf.gb_gbsh li.gbt.gbtn {
  visibility: visible !important;
  top: 7px;
}

/* hangouts button */
.RWa.ohUUPb {
  top: 44px !important;
}
.RWa.ohUUPb .Teb {
  background-color: #fff;
}


/* profile bar */
.oBgpme.AYoUUe {
  top: 44px !important;
}
    
/* favorite posts for Google+ extension */
.fpMenuBar.fpMenuBarFixedGbar {
  top: 0 !important;
  background-color: #fff;
}
                    
}
