css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("amazon.com") {

    #ap_container,
    #leftnav,
    #navFooter,
    header,
    #avod-hd-banner,
    .dl-header,
    .dl-details-hidden,
    #watchlistForm,
    .avod-wrapped-side-box div.line,
    .avod-feedback,
    .also-avail,
    #avod-gift-bar,
    #available-seasons,
    #prod-details .stars,
    .amabot_widget,
    #consumption-sims-feature,
    div.bucket,
    div.reviews,
    .dp-main-bottom,
    .buy-box,
    .dv-action.dv-watchlist .dv-button-inner,
    .episode-status .prime-logo-small,
    .available-string,
    .dv-meta-info.size-small,
    .dv-extender-see-more,
    .tancaccept,
    #reviewContainer,
    #revDivider,
    #rhf {
        display: none !important;
    }

    table#avod-ep-list-header th,
    div#avod-ep-list-rows table.episodeList td {
        padding-top: 3px !important;
        padding-bottom: 3px !important;
        color: #f1f1f1 !important;
        line-height: normal !important;
    }
    div#avod-ep-list-rows table.episodeList tr:hover td {
        background-color: #101010 !important;
    }
    div#avod-ep-list-rows table.episodeList td,
    div#avod-ep-list-rows table.episodeList td div {
        font-size: 13px !important;
    }
    tr.episodeRow td {
        border-top: 1px solid #060606;
        border-bottom: 1px solid #767676;
    }

    div#avod-ep-list-rows table.episodeList td div.avod-button a,
    div#avod-ep-list-rows table.episodeList td div.avod-button a table,
    div#avod-ep-list-rows table.episodeList td div.avod-button a table td {
        font-size: 10px !important;
    }

    #image-wrapper {
        margin-bottom: 0 !important;
    }

    #avod-main {
        min-height: 0 !important;
    }

    body,
    #avodlib,
    #main {
        margin: .5em auto !important;
        padding: 0 !important;
        width: 95% !important;
    }

    #bc-container,
    div.nav-stripe {
        background-color: #1B1B1B !important;
        background-image: linear-gradient(to bottom, #222222, #111111) !important;
        background-repeat: repeat-x !important;
    }
    #bc-container {
        padding: 5px 10px !important;
        border-top: 1px solid #333 !important;
        border-bottom: none !important;
    }
    #bc-container h1,
    #bc-container a {
        color: #999 !important;
    }

    div.nav-strip {
        padding: 5px 10px !important;
        border: none !important;
        line-height: 20px !important;
    }

    body {
        background-color: #333 !important;
    }
    .dv-extender,
    .dv-extender p,
    .selected-episode > .dv-extender,
    .selected-episode > .dv-extender > p {
        color: #f1f1f1 !important;
        width: 100% !important;
    }
    .dv-badge.subtitles-icon,
    .dv-badge.content-rating,
    .dv-badge.runtime
    {
        display: none !important;
    }
    .dv-badge.release-date {
        font-size: 18px !important;
        display: block !important;
        float: right !important;
        margin-bottom: .5em !important;
        padding: 3px 5px !important;
        min-width: 140px !important;
        border-color: #626262 #222 #222 #626262 !important;
        background-color: #4d4d4d !important;
    }
    .episode-list > ul > li { clear: both !important; }
    
    .episode-list li {
        border-top-color: #666 !important;
    }

    body,
    #avodlib,
    #main,
    #all-items,
    #all-items th,
    #all-items td {
        background-color: #333;
        color: #999;
        font-size: 18px;
    }

    #all-items {
        border-spacing: 5px 0 !important;
        border-collapse: separate !important;
    }

    #all-items tr,
    #all-items td,
    #all-items td {
        padding-top: 5px !important;
    }

    #all-items td {
        border: none !important;
    }

    div.lib-item {
        margin: 0 0 10px;
        padding: 20px 5px !important;
        min-height: 240px;
        height: 240px;
        border-radius: 4px 4px 4px 4px;
        background: rgb(255,255,255);
        background: -moz-linear-gradient(top,  rgba(255,255,255,1) 59%, rgba(229,229,229,1) 100%);
        background: -webkit-gradient(linear, left top, left bottom, color-stop(59%,rgba(255,255,255,1)), color-stop(100%,rgba(229,229,229,1)));
        background: -webkit-linear-gradient(top,  rgba(255,255,255,1) 59%,rgba(229,229,229,1) 100%);
        background: -o-linear-gradient(top,  rgba(255,255,255,1) 59%,rgba(229,229,229,1) 100%);
        background: -ms-linear-gradient(top,  rgba(255,255,255,1) 59%,rgba(229,229,229,1) 100%);
        background: linear-gradient(to bottom,  rgba(255,255,255,1) 59%,rgba(229,229,229,1) 100%);
        box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.2), 0 2px 5px rgba(0, 0, 0, 0.3);
    }

    #all-items a,
    table#all-items div.lib-item > div.title > a {
        display: block;
        margin: 0 auto !important;
        padding: 0 10px !important;
        width: 85% !important;
        background-color: transparent !important;
        color: #333 !important;
        text-shadow: 1px 1px 1px #ffffff;
        font-size: 20px !important;
        line-height: 23px !important;
    }

    #all-items a:hover,
    table#all-items div.lib-item > div.title > a:hover {
        padding: 10px 0 !important;
        border: medium none;
        border-radius: 3px 3px 3px 3px;
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.8);
        box-shadow: 0 1px 0 rgba(255, 255, 255, 0.4), 0 1px 7px 0 rgba(0, 0, 0, 0.8) inset;
        oolor: #FFFFFF !important;
    }

    div.img-container {
        margin-bottom: 1em !important;
    }
    
    table#all-items > tbody > tr > td > div.lib-item > div.img-container > div.qv-details,
    table#all-items > tbody > tr > td > div.lib-item > div.img-container > div.qv-details:hover {
        display: none !important;
    }
    
    #a-page > .aiv-wrapper > .aiv-container-limited > .dv-dropdown-single {
        font-size: 30px !important;
        color: white !important;
    }
    
    .carousel-control {
        background-color: #81d6fb !important;
    }
    .carousel-progress-bar-track {
        background-color: #111 !important;
    }
    .carousel-progress-bar-scrubber {
        background-color: #333 !important;
    }

}
