@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("spiegel.de") 
{
    
.column-small, .sticky-overlay
{ 
    display: none !important;
}

DIV.sticky-home-link.article-function-social-media, DIV.sticky-content, DIV.page-overscroll-area, DIV.article-function-social-media, DIV.social-bar-wrapper {
display:none !important; }

[id*=-overscroll-]
{ 
    display: none !important;
    height: 3000px !important;
}
    
    
DIV#js-article-column.column-wide {
    float: left !important;
    width: 849px !important;
}
}
