css:
@-moz-document domain('indifference.boards.net') {
.welcomebg img {
     display: none !important;
}
#google_ads_iframe_728x90_ROS, #google_ads_div_728x90_ROS {
     display: none !important;
}
a:visited, a:link, a:visited {
      !important;
     text-decoration:none !important;
     font-weight:normal;
}
a:hover {
     color:#FF0000 		    !important;
     text-decoration:underline !important;
     font-weight:normal;
}

[id=navigation-menu] {
 	background: black !important; 
}

#navigation-menu > ul li a {
  	
  	font-weight: bold !important;
 	color: white;
}

.state-active {
  	background: #FFCC33 !important;
  	color: black !important;
  	-moz-border-radius: 3px;
	border-radius: 3px;

}
  
.reply-button, [role=button] {
    background: #FFCC33 !important;
    color: black !important;
    font-weight: bold !important;
}
    
td.left-panel {
    background: #FFCC33 !important;
  	-moz-border-radius: 10px;
	border-radius: 10px;
}

.content.cap-bottom {
  	border: 0px solid white !important;
}

textarea[name=message]:focus,
input.search-input:focus {
  border: 1px solid #FFCC33 !important;
  box-shadow: 0px 0px 3px #FFCC33;
}
    
body {
     background-image:url('http://morristsai.com/assets_c/2010/06/GoogleBackgroundBlack-thumb-400x300-2147.png') !important; !important;
}

.title-bar {
	background-color: black !important; 
}


.title-bar > h2,
.title-bar > h1
{
   color: #FFD700 !important; 
}

.windowbg {
     background:#b4b4b4 !important;
}
.windowbg2 {
     background:#959595 !important;
}
td {
     color:#000000 !important;
}
.welcomebg {
     background:#000000!important;
     color:#ffffff !important;
     width:50%;
}
.welcomebg a {

     text-decoration: underline !important;
     font-weight: bold !important;
}
.catbg {
     background:#999999 !important;
}
.menubg {
     background:#999999 !important;
}
.bordercolor{
     border: 1px solid #000000!important;
}
.nav, .group0, .group1 {
     font-weight:bold !important;
}
.titlebg, .newstitlebg {
     background:#666666 !important;
}
.quote {
     background: #888888 !important;
     color:#333333 !important;
     font-size:8.5pt !important;
}
body center font, body center a, body center iframe {
     display:none !important;
}
.nav {
     font-size:8pt !important;
}
#fscroller {font-size:14pt !important; height:20px !important;}
a
.container.legend{
 	
} 

[id=banner], footer, .legend {
  display:none !important;
}

}
