css:
@-moz-document url-prefix('http://awbw.amarriner.com/') 
{

body             {background:url(http://img15.hostingpics.net/pics/937949awbwbg.jpg) repeat !important;color:#dcdcdc !important;}
body.popup       {color:#000330;background:rgba(0,0,0,0.0) !important;}
.h2              {color:#dcdcdc !important;}

a:link           {color:rgb(100,100,100) !important;}
a:visited        {color:rgb(100,100,100) !important;}
a:hover          {color:rgb(170,170,170) !important;}
a.norm b   {color:rgb(244,199,18) !important;}
a.norm i    {color:rgb(110,7,7) !important;font-weight:normal !important;}

.small_text      {color:rgb(110,110,110) !important;}
.small_text_11   {color:rgb(140,140,140) !important;}
.small_text_11 b {color:rgb(170,170,170) !important;}

table            {background:url(http://img15.hostingpics.net/pics/937949awbwbg.jpg) repeat !important;}
table.whiteborder{border:solid 0px !important;background:rgba(0,0,0,0.0) !important;}

td.menu a        {font-family:Arial,Verdana,Helvetica,Geneva,sans-serif;font-size:10px;font-style:normal;}

td               {color:#999999 !important;}
td.menu          {background:rgba(0,0,0,0.0) !important;color:#aaaaaa !important;border-color:rgba(0,0,0,0.0) !important;}
td.menu br + img {height: 0 !important;width: 0 !important;padding-left: 125px !important;padding-top: 1px !important;
	              background: url(http://img15.hostingpics.net/pics/345102line.png) repeat !important;}
	              
td.menu b + img  {height: 0 !important;width: 0 !important;padding-left: 125px !important;padding-top: 1px !important;
	              background: url(http://img15.hostingpics.net/pics/345102line.png) repeat !important;}

td.borderwhite   {border:0px !important;background:rgba(0,0,0,0.0) !important;color:#000330;}
td.borderwhite a {color:#aaaaaa !important;}
td.bordergrey    {border:0px !important;background:rgba(0,0,0,0.0) !important;color:#000330;}
td.smallheader   {background:rgba(0,0,0,0.0) !important;color:#000330;}
td.small         {border-right:0px !important;border-left:0px !important;}

td.unit_info     {background:rgba(0,0,0,0.0) !important;}

#overDiv                     {border:2px solid rgb(0, 102, 204) !important;}
#overDiv .small_text a       {color:rgb(244,199,18) !important;font-size:10px !important;}
#overDiv .small_text a:hover {color:rgb(244,199,18) !important;}
#overDiv .small_text         {color:rgb(150,20,20) !important;}
}
