css:
@-moz-document url("http://w3schools.com/"), url("http://www.w3schools.com/"), regexp("http://(www\\.)?w3schools\\.com/(html|css|css3|js|cssref|xpath)/(?!(tryit|playit)).*") {
body{
  background: #000 !important;
  color:#777777 !important;
}
a{
  color: #777777 !important;
}
hr:not([class^="arrowhr"]){
  display: none!important;
}


  

#leftcolumn{
  position:fixed !important;
  left:0px !important;
  top:20px !important;
  background: #000 !important;
  color: inherit !important;
  width: 12% !important;	
  height: 90% !important;
  overflow-y: scroll !important;
  overflow-x: hidden! important;
  border-top: 2px dashed #151515 !important;
  border-left: 2px dashed #151515 !important;
  border-bottom: 2px dashed #151515 !important;
  border-radius:12px !important;
  padding-top: 5px !important;
  padding-left: 2px !important;
  padding-bottom: 5px !important;
}
#leftcolumn>a[target="_top"]:hover,.notranslate>a[target="_top"]:hover{
  color: inherit !important;
  background: #151515 !important;
  border: none !important;
  text-decoration: none !important;
}
#leftcolumn>a[target="_top"], div[class="expandingMenuItem"]>a{
  color: inherit !important;
  background: inherit !important;
  border: none !important;
  text-decoration: none !important;
}

div[id="top"]{
  margin-left: 20% !important
}

h2[class*="left"]{
  color: inherit !important;
  background: #252525 !important;
  font-weight: 800 !important;
  border: none !important;
  border-top-left-radius:  5px !important;
  padding-left: 15px !important;
}

.notranslate>a[target="_top"]{
  color: inherit !important;
  background: inherit !important;
  border: none !important;
}

 .expandingMenuHeader{
  color: inherit !important;
  background:none !important;
  border: none !important;
}
.expandingMenuHeader:hover{
  color: inherit !important;
  background: #252525 !important;
  border: none !important;
}
div[class^="expandingMenuItem"]:hover{
    color: inherit !important;
  background: #252525 !important;
  border: none !important;
}
div[class^="expandingMenuItem"]:hover>a{
  text-decoration: none !important;
}


div[class$="expandingMenuSelected"]{
    color: inherit !important;
  background: #151515 !important;
  border: none !important;
}





#mainLeaderboard, #rightcolumn{
  display: none!important;
}
#topRight{
  display: none !important;
}
#page{
  background: inherit !important;
  color: inherit !important;
  width: 90% !important;
  border: none !important;
}


a[class="tryitbtn"]{
  color: #fff !important;
}
a[class="tryitbtn"]:hover{
  color: #8ac007 !important;
}
td>a[class="playitbtn"]{
  color: #fff !important;
  background: #8ac007 !important;
  border-color: #8ac007 !important;
}
td>a[class="playitbtn"]:hover{
  background: #fff !important;
  color: #8ac007 !important;
  border-color: #8ac007 !important;
}

#main{
  background: inherit !important;
  color: #777777 !important;
  border: none !important;
}
#topnav{
  background: #151515 !important;
  color: #8ac007 !important;
  border: none !important;
  border-radius: 4px !important;
}
#topnav a[target="_top"]:hover, h2{
  color: #8ac007 !important;
}
#main>h1{
  color: #777777 !important;
}
.example{
    background: #151515 !important;
    border: none !important;
    color: inherit !important;
  	border-radius: 6px !important;
  }
 
div[class*="code"]{
  
  background: #2b3859 !important;
  border: 2px inset #444 !important;
  color: #8ac007 !important;
}

th{
    color: #888 !important;
    border: 1px solid #555555 !important;
}
table[class*="notranslate"], tr:not([bgcolor]), td:not([bgcolor]){
    background: #151515 !important;
    color: #777777 !important;
    border: 1px solid #555555 !important;
}
tr[bgcolor], td[bgcolor]{
    color: inherit !important;
    border: 1px solid #555555 !important;
}



img[alt="W3Schools.com"],img[alt="Examples"],img[alt="W3Schools Certification"]{
  visibility: hidden !important;
}
#topLogo{
  margin-top: 8px !important;
background-repeat: no-repeat;
background-image: url("http://i.imgur.com/DTEckkS.png")!important;
}
.tryit_ex{
  margin-top: 8px !important;
background-repeat: no-repeat;
background-image: url("http://i.imgur.com/A2nZOLi.png")!important;
}
div[style="float:left;width:224px;"]{
  margin-top:40px !important;
background-repeat: no-repeat;
background-image: url("http://i.imgur.com/WCW6bMs.png")!important;
}
}

@-moz-document url-prefix("http://w3schools.com/html/") {
hr{
  display: none!important;
}
img[alt="HTML"]{
  visibility: hidden !important;
}

.tutintro{
height: 132px !important;
background-repeat: no-repeat !important;
background-image: url("http://i.imgur.com/senoUb8.png")!important;
}
}

@-moz-document url-prefix("http://w3schools.com/css/"), url-prefix("http://w3schools.com/cssref/") {
img[alt="CSS"]{
  visibility: hidden !important;
}
.tutintro{
height: 132px !important;
background-repeat: no-repeat !important;
background-image: url("http://i.imgur.com/v0Cy1f0.png")!important;
}
}

@-moz-document url("http://w3schools.com/") {
img[alt="HTML5"]{
  visibility: hidden !important;
}
div[style*="frontbanner960_2.jpg"]{		
  background: url("http://i.imgur.com/R8DjFRj.png") !important;
  background-repeat: no-repeat !important;
}
#maincolumn{
  margin-left: 10% !important;
}
a[style] > span{                                
  background: inherit !important;
  color: #8ac007 !important;
}
div[id^="headerdiv"]>div[style="height:100px;"]{ 
  background: #151515 !important;
  padding: 6px !important;
  border-radius:6px !important;
}
div[style^="margin-top:47px;margin-left:65px"]{ 
  background: inherit !important;
  border: none !important;
}
#frontbox{
  background: #151515 !important;
}
div[style="width:130px;float:left;margin-right:15px;"]{
    background: url("http://i.imgur.com/m71CB5b.png") !important;
    	background-repeat: no-repeat !important;
  	padding-top: -100px !important;
}
}
