css:
@-moz-document domain("flickr.com") {

/* Light grey background for the whole photo page */
body {
	background-color: #e4e4e4 !important;
}

#main {
    background: transparent !important
}

/* Top menu bar - move to right to clear avatar, and clear black background */
#global-nav {
    background: transparent !important;
	position: fixed !important;
	margin-left:125px !important;
	width: 93% !important;
	height: 40px !important;
}
 
/* Set text of top-menu items to black */
#global-nav span {
	color: #000 !important;
}

/* Lighten lower menu bar */
UL.nav-links.clearfix {
	background: transparent !important;
}

/* Clear the outline border from the lower menu bar */
.subnav-holder {
    border: none !important;
	background: transparent !important;
}

/* Remove the banner photo */
.cover-photo-holder {
    display: none !important;
}

/* Shrink the banner area */
.subnav-middle {
	background: transparent !important;
    height: 125px !important;
}

/* Re-position the username & 'stats' strip */
.subnav-refresh h1 {
    position: relative !important;
    top: 6px !important;
    font-size: 1.2em !important;
    text-shadow: none !important;
    color: grey !important;
}
   
.subnav-refresh h2 {
    position: fixed !important;
    top: 48px !important;
    font-size: 0.7em !important;
    text-shadow: none !important;
    color: grey !important;
}

/* Make lower menu text black */
.subnav-refresh ul.nav-links li.sn-navitem {
    background: transparent !important;
    border-right: none !important;
    border-left: none !important;
    font-size: 0.8em !important;
    color: black !important;
}

/* Remove drop shadow from lower menu text */
.subnav-refresh ul.nav-links li.sn-navitem a {
    text-shadow: none !important;
}
    
/* Make the edit contact link more prominent */
.badge_edit_contact {
    color: blue !important;
}

/* Hide the small avatar icon if the user hasn't uploaded a larger one */
.sn-avatar-ico {
display: none !important;
}

/* Lighten photo area */
#photo-container {
	background: transparent !important;
}

#photo {
	background: transparent !important;
}

/* Make the menu buttons show more clearly */
#photo-actions  {
    position: relative !important;
    background: transparent !important;
	background-color: #ddd !important;
}

/* Move photo title inwards and black text on light grey */
.photo-title {
    background: transparent !important;
	margin-left: 22% !important;
	color: #000 !important;
}

/* Move description inwards */
.photo-desc {
	margin-left: 100px !important;
}

/* Move comments, invites, favest etc. inwards */
#invites-and-comments {
	margin: 0px 0px 0px 100px !important;
}

/* Increase the height of the comment text input box */
#comments .adding-comment textarea {
    height: 200px !important;
}

/* Space out sidebar (photo details etc.) a little more */
#sidebar.wide-sidebar {
    width: 700px !important;
    margin-left: -750px !important;
}
    
/* Make previous/next buttons less obtrusive */
a#nav-bar-prev {
	opacity: .3 !important;
	height: 800px !important;
}

a#nav-bar-next {
	opacity: .3 !important;
	height: 800px !important;
}
/* Move the hover-over/pop-up title below the image */
.meta {
    position: static !important;
}

.photo-display-item {
    background-color: #e4e4e4 !important;
    border-right: 4px solid #e4e4e4 !important;
    border-left: 4px solid #e4e4e4 !important;
    padding-top: 10px !important;
    padding-bottom: 30px !important;
}
}
