css:
@-moz-document domain("nsider2.com") {
#ipbwrapper {
	background-color: #000000; 
}

#content {
 	background-color: #333333; 
}

.ipsBox, tbody tr td, .unread td{
 	background-color: #222222;
  	border-color: #333333;
}

.ipsSideBlock {
 	background-color: #222222; 
}

.ipsBox_container {
 	background-color: #333333;
  	border-style: none;
}

.ipsType_pagedesc {
 	color: #FFFFFF;
}

.subforums li {
 	background-color: #000000; 
	  padding:2px;
}

a {
 	color: #FFFFFF; 
}

ul.mini_pagination li a { 
	background-color: #222222;
}

.post_block {
 	background-color: #333333; 
  	margin-bottom: 20px;
	border-bottom-style: none;
}

.post_body .post {
 	color:#FFFFFF; 
}

div.blockquote, p.citation {
 	background: #333333;
}

p.citation {
	border-width:1px;
  	border-style:solid;
  	
}

.author a{
 	color:#000000; 
}

.unread .altrow{
 	background-color: #222222; 
}

.list_content a, #links li a {
	color: #3333AA;
}

.list_content a:hover, #links li a:hover {
	color: #7777EE;
	text-decoration:underline;	
}
}
