}

p.citation {
    border-color: rgb(255, 255, 255) ;
    border-style: none ;
    color: rgb(247, 164, 180) ;
    border-left: 2px solid rgb(43, 25, 25) ;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(56, 51, 52)), color-stop(100%,rgb(177, 25, 37))) ;
    border-top: 2px solid rgb(43, 25, 25) ;
    border-right: 2px solid rgb(43, 25, 25) ;
    -moz-border-radius: 5px 5px 0 0 ;
    -webkit-border-radius: 5px 5px 0 0 ;
    border-radius: 5px 5px 0 0 ;
    font-weight: bold ;
    padding-top: 8px ;
    padding-right: 10px ;
    padding-bottom: 8px ;
    padding-left: 10px ;
}

div.list_content a {
    color: #c9bfc9;
}

a.ipsType_small {
    color: #c9bfc9;
}

a {
    color: #ffffff;
}

#ipbwrapper {
    background-color: #605e6b;
}

a.ipsButton_secondary {
    color: #616161;
    line-height: 22px;
    font-size: 12px;
    padding: 0 10px;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(43, 34, 34)), color-stop(100%,rgb(255, 0, 0)));
    border: 1px solid #dbdbdb;
    -moz-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    -webkit-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    display: inline-block;
    white-space: nowrap;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
}
