css:
@-moz-document domain("apps.facebook.com") {
/* FaceBook Game */

#rightCol { /* 隱藏右列贊助廣告、你的應用程式、其他遊戲 */
  display: none;
  }

#contentArea { /* 背景色(FB 內定 ID ，如果遊戲公司本身另設 ID 請自行增加語法或更改) */
  background-color: #edeff4;
  }
}

@-moz-document domain("www.facebook.com") {
/* FaceBook 首頁、個人首頁...等 */

.ego_column { /* 隱藏右列贊助廣告 */
  display: none;
  }

.ego_page .UIStandardFrame_Content { /* 動態時報相片(贊助廣告移除後版面置中，在 Firefox 上無作用) */
  width: 100%;
  }
}

@-moz-document domain("kingdom-knights-game.uj.com.tw"), domain("zynga.com"), domain("royal-us.socialgamenet.com"), domain("krd.nexon.com") {
/* FaceBook Game - Kingdom Knights-王國騎士團 */

div[id="applifier_bar"] { /* 移除上方廣告 */
  display: none;
  }

/* FaceBook Game - FarmVille 2 */

div[id="zbar"] { /* 移除上方廣告 */
  display: none;
  }

div[id="zap-FB_FARM2_BAN_BAC"] { /* 移除下方廣告 */
  display: none;
  }

/* FaceBook Game - Royal Story */

div[id="royal-banner1"] { 
  display: none;
  }

/* FaceBook Game - KartRider Dash */

div[class="banner ad-nexon"] { 
  display: none;
  }
}
