css:
@-moz-document domain('ex.ua') {
/*
CSS-style для сайта EX.ua.
*/
[href*='.adfox.ru/'],[src*='.adfox.ru/'],
[href*='.admaster.net/'],[src*='.admaster.net/'],
[href*='.adriver.ru/'],[src*='adriver.ru/'],
[href*='.advideo.com.ua/'],[src*='.advideo.com.ua/'],
[href*='mts.media.com.ua'],[src*='mts.media.com.ua'],
[href^='http://ads.'],[src^='http://ads.'],
[src*='t.newseek.org/'] {
	display: none !important;
	}
a[onClick*="social"], div[id*="plusone"], #announce, #ad_block, #ad_block_1, #ad_block_2, #ad_block_3, #ad_block_4, a[href*="ad_click"], a[href*="b.ex.ua/*/index.css"], #index_box {
	display: none !important;
	}
/* Выше удаление рекламы, дальше темная тема для сайта, кому ненада - закоментируйте или удалите все что ниже */
body {
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	color: #a6a6a6 !important;
	background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAACXBIWXMAABwlAAAcJQHgtf5oAAAAH0lEQVR42mNgYGD4LyIiAscMUlJSqAJwBlQlXACmEgB2khNdClje5gAAAABJRU5ErkJggg==") repeat !important;
	}
#search_box { 
	background-image: none !important;
	height: 60px !important;
	width: 1000px !important;
}
#search_line {
	left: 10px !important;
	top: 10px !important;
}
#search_text {
	width: 500px !important;
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	font-weight: bold !important;
}
#search_button {
	top: 10px !important;
	left: 530px !important;
}
#search_help {
	top: 10px !important;
	left: 620px !important;
}
[id*='announce'] {
	display:none !important;
	}
td {
	font-size: 11pt !important;
	height: 20px !important;
	}
.menu {
	background: -webkit-linear-gradient(rgb(30, 30, 30), rgb(60, 60, 60)) !important;
	background: linear-gradient(rgb(30, 30, 30), rgb(60, 60, 60)) !important;
	box-shadow: 0px 0px 5px rgba(0,0,0,5) inset !important;
	border-bottom: 0px !important;
	border-bottom-color: #000 !important;
	border-bottom-style: solid !important;
	border-bottom-width: 1px !important;
	}
.menu a {
	font-size: 9pt !important;
	color: #AEAE00 !important;
	font-weight: bold !important;
	}
.menu a:hover {
	color: #FFFF80 !important;
	font-weight: bold !important;
	text-decoration:none !important;
	}
a {
	color: #0080FF !important;
	}
a:hover {
	color: #00D900 !important;
	}
h1 {
	color: #FFA07A !important;
	font-family: 'Segoe UI Semibold', 'Lucida Sans Unicode' !important;
	font-size: 11pt !important;
}
h2 {
	color: #1E90FF !important;
	font-family: 'MS Reference Sans Serif', 'Liberation Sans' !important;
	font-size: 11pt !important;
	font-weight: 800 !important;
	text-decoration: underline !important;
}
p b {
	font-family: Arial !important;
	font-size: 10pt !important;
}
.include_0 tr td { 
	border: 1px solid #353535 !important;
	}
.include_1 tr td { 
	border: 1px solid #353535 !important;
	}
.list tr td, .comment tr td { 
	border-bottom: 1px dashed #353535 !important;
	}
.panel tr td { 
	border-bottom-color: #353535 !important;
	border-left-color: #353535 !important;
	border-right-color: #353535 !important;
	border-top-color: #353535 !important;
	}
.list tr td {
	font-style: italic !important;
}
.button, .r_button a {
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	font-weight: 400 !important;
	border-radius: 3.5px !important;
	border: 1px solid rgb(0,0,0) !important;
	box-shadow:  0px 1px 1px rgba(255, 255, 255, 0.2) inset, 0px 1px 3px rgba(0, 0, 0, 0.5) !important;
	background: rgba(12,12,12,1)linear-gradient(rgba(255, 255, 255, 0.21),rgba(255, 255, 255, 0.13) 49%,rgba(255, 255, 255, 0.094) 51%, rgba(255, 255, 255, 0.05)) !important;
	color: #a6a6a6 !important;
	text-shadow: none !important;
	}
.button:hover, .r_button a:hover {
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	font-weight: 400 !important;
	color: #000 !important;
	background-color: #00F000 !important;
	}
.small_button, .r_button_small a {
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	font-weight: 400 !important;
	border-radius: 3.5px !important;
	border: 1px solid rgb(0,0,0) !important;
	box-shadow:  0px 1px 1px rgba(255, 255, 255, 0.2) inset, 0px 1px 3px rgba(0, 0, 0, 0.5) !important;
	background: rgba(12,12,12,1)linear-gradient(rgba(255, 255, 255, 0.21),rgba(255, 255, 255, 0.13) 49%,rgba(255, 255, 255, 0.094) 51%, rgba(255, 255, 255, 0.05)) !important;
	color: #a6a6a6 !important;
	text-shadow: none !important;
	}
.small_button:hover, .r_button_small a:hover {
	font-family:'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	font-weight: 400 !important;
	color: #000 !important;
	background-color: #00F000 !important;
	}
html input {
	border: 1px solid #5c5a46 !important;
	font-family: Arial, 'Liberation Sans', sans-serif !important;
	background-color: #191919 !important;
	border-bottom-width: 1px !important;
	border-left-width: 1px !important;
	border-right-width: 1px !important;
	border-top-width: 1px !important;
	color: #B8B872 !important;
	font-family: use-lang-def;
	font-size: 13px !important;
	font-weight: 800 !important;
	padding: 0px !important;
	text-align: default;
	text-indent: 0px;
	text-transform: none;
	}
html textarea {
	font-family: 'Segoe UI', 'Helvetica Neue', Helvetica, 'Liberation Sans', sans-serif !important;
	background-color: #252525 !important;
	border: 1px solid #5c5a46 !important;
	border-bottom-width: 1px !important;
	border-left-width: 1px !important;
	border-right-width: 1px !important;
	border-top-width: 1px !important;
	color: #c0c0c0 !important;
	font-family: use-lang-def;
	font-size: 16px !important;
	font-weight: 400;
	line-height: normal;
	overflow-wrap: break-word;
	padding: 0px !important;
	resize: both;
	text-align: default;
	text-indent: 0px;
	text-transform: none;
	}
}
