css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("www.ynet.co.il") {

#mainCont > br{
display:none;
}
#mainCont{
margin:auto;
}
#mainCont > table{
background:white;
width:980px !important;
padding:0;
margin:0;
}

#main > .area.content .spacer {
margin-left:0 !important;
}

#main>div:nth-child(3)>div>div:nth-child(2)>div
{
display:none !important;
}

#main>div:nth-child(3)>div>div:nth-child(2)>div:nth-child(2)
{
display:block !important;
width:100% !important;
-moz-column-count:2 !important;
}


#main>div:nth-child(3)>div>div:nth-child(3) > div  > div
{
display:none;
margin-left:0 !important;
}

#main>div:nth-child(3)>div>div:nth-child(3) > div:nth-child(2) > div:nth-child(1)
{
display:block !important;
}
#main > .area{
float:none !important;
}
#main{
background: #fff !important;
padding: 2em  !important;
margin: auto  !important;
}
body{
background: #000 !important;
}
#ads\.300x600\.1{
display:none !important;
}
.mgal_citems{
height: auto !important;
overflow:auto !important;
}
.mgal_citems > a {
display:block !important;
}
.mgal{
height: auto !important;
}
.mgal_titems{
display:none !important;
}
.mgal_citems > button{
display:none !important;
}
.mgal_fitems{
display:none !important;
}
.mgal_citems > a > .mgal_ctitle > span{
background: rgba(100, 100, 100, 0.7);
padding: 0 10px;
}
}
