 **************************************************************/ 
@namespace url(http://www.w3.org/1999/xhtml);


/************************************************************** 
 *	PrÃ©fixe de l'URL
 **************************************************************/ 
@-moz-document url-prefix("http://www.centaurius.net/alpha/index.php") {


/**************************************************************
 *	MODIFICATIONS GENERALES DU JEU (hors forum)
 **************************************************************/

th {border:none!important;}
div, input, textarea {-moz-border-radius:5px!important;}
th {-moz-border-radius:5px 5px 0px 0px;}

/* boutons et zones de texte */
input, textarea {-moz-opacity:.70;}
input:hover,
input:active,
input:focus,
textarea:hover,
textarea:active,
textarea:focus {
-moz-opacity:1.0!important;
}

/* haut de page */
.PgPannelBlk {
border:2px solid black!important;
background:none!important;
background-color:#0C0C0C!important;
background-image:url("http://www.exystence.fr/imgs/bg_page_top.png")!important;
background-repeat:repeat-x!important;
-moz-opacity:.90;
}

/* transparence de la barre de navigation */
.PgNavBar{-moz-opacity:.70;}

/* bas de page */
#PgFootDeco{display:none!important;}
.PgPannelFootExcept{height:50px!important;}

/* console transparente */
#PgConsole{-moz-opacity:.91;}


/**************************************************************
 *	MODIFICATIONS SUR LE FORUM
 **************************************************************/



/**************************************************************
 *	PARTIES FONCEES (gris foncÃ©)
 **************************************************************/
.TopicUser
{
background-image:none!important;
background-color:#5F5F5F!important;
color:black!important;
-moz-opacity:.70;
}

/**************************************************************
 *	PARTIES CLAIRES (gris clair)
 **************************************************************/
.TopicPost
{
background-image:none!important;
background-color:#8F8F8F!important;
color:black!important;
}

/************************************************************** 
 *	BLOCS 'Quote' & 'Code'
 **************************************************************/
.forQuote
{
background-color:#5F5F5F!important;
color:white!important;
border-color:black!important;
}
.forCode
{
background-color:white!important;
color:black!important;
border-color:grey!important;
}

/**************************************************************
 *	POST-IT & ANNONCES (rouge)
 **************************************************************/
.Section h6
{
color:#8F0000!important;
font-weight:bold!important;
}

/************************************************************** 
 *	LIENS (blanc)
 **************************************************************/
.TopicUser a,
.TopicPost a
{
color:white!important;
}
.button,
.LibButton
{
color:white!important;
padding:5px!important;
border-style:solid!important;
border-width:1px!important;
border-color:grey!important;
background-color:#353849!important;
-moz-border-radius:5px;
}

/************************************************************** 
 *	LIENS SURVOLES
 **************************************************************/
.TopicUser a:hover,
.TopicPost a:hover
{
color:white!important;
text-decoration:underline!important;
}
.button:hover,
.LibButton:hover
{
color:#65A6DC!important;
background-color:black!important;
text-decoration:none!important;
border-color:#65A6DC!important;
}

}
