css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
    .player-width{
        width: 800px !important;
    }
    
    .player-height{
        height: 470px !important;
    }
    
    .site-left-aligned.guide-enabled #player, .site-left-aligned.guide-enabled #watch7-main-container, #player, #watch7-main-container{
        margin-left: auto !important;
        margin-right: auto !important;
        padding: 0 !important;
        width: 800px !important;
    }
    
    #player, #watch7-main{
        min-width: 800px !important;
    }
    
    #player.full-frame{
        min-width: 1 !important;
    }
    
    #player-legacy{
        margin-left: auto !important;
        margin-right: auto !important;
        padding: 0 !important;
        width: 800px !important;
        min-width: 800px !important;
    }
    
    #player.full-frame{
        padding-left: 0px !important;
        height: 100% !important;
        width: 100% !important;
    }
    
    #logo-container{
        margin-right: 0px !important;
    }
    
    #yt-masthead-content{
        margin-left: auto !important;
        margin-right: auto !important;
        width: 800px !important;
    }
    
    .watch-sidebar, #guide, #watch-discussion{
        display: none !important;
    }
}
