css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("downtr.net") {

/*--------------------------------------------------
	Downtr.net layer Stylesheet
	version:   1.00
	date:      18/Jul/08
	author:    [shirazi]
	email:     [nitevish@gmail.com]
----------------------------------------------------
	 _____ _____ _____ _____ _____ _____ _____ 
	|   __|  |  |     | __  |  _  |__   |     |
	|__   |     |-   -|    -|     |   __|-   -|
	|_____|__|__|_____|__|__|__|__|_____|_____|

*/

body{
   background-color:#111 !important;
}
html>body>div>div>div>center,
div#userbar input[style*="visibility: hidden;"],
div#footer div, div.blq center iframe,
span[style*="font-family: Impact; font-size: 18pt;"],
table[style*="width: 200px; height: 150px;"],
h2 img[src*="http://i37.tinypic.com/24mck1h.png"],
img[src*="/templates/indir/images/bbb.png"],
div#dle-content>center, iframe,
div#mnbbgr table td#menu p>table, 
div#mnbbgr table td#menu table[width="100%"],
div#mnbbgr table td#menu>div>p>span[style],
div.blq td[height*="46"] img
{
   display:none !important;
}
div#navbar{
   display:block !important;
   position:relative !important;
   z-index:999 !important;
   top:-120px !important;
   width:47% !important;
}
div#navbar span#dle-speedbar{
   font-size:11px !important;
   line-height:16px !important;
   color:#fff !important;
}
div#navbar span#dle-speedbar a{
   color:#4E6679 !important;
}
div#navbar span#dle-speedbar a:hover{
   color:#2E3C47 !important;
}

div#container{
   margin:12px 75px !important;
   min-width:845px !important;
   -moz-border-radius:10px !important;
   background-image:none !important;
   padding-top:3px !important;
}

/* userbar styles */
div#userbar{
   height:38px !important;
   margin:0 !important;
   padding:1px 0 0 0 !important;
   line-height:22px !important;
   text-align:right !important;
   position:relative !important;
   z-index:99 !important;
   border-style:none !important;
   font-size:11px !important;
   color:#fff !important;
   top:5px !important;
   -moz-border-radius:10px !important;
   background-color:#182325 !important;
}
div#userbar a{
   color:#4E6679 !important;
}
div#userbar a:hover{
   color:#2E3C47 !important;
}
div#userbar div{
   padding:0 !important;
}
div#userbar input.mainforms{
   background-color:#283943 !important;
   border-color:#132028 !important;
   color:#fff !important;
   font-size:11px !important;
   top:-2px !important;
   position:relative;
}
div#userbar input.mainforms:hover{
    background-color:#697781 !important;
}
div#userbar input.bbcodes{
   padding:1px 4px !important;
   background-color:#35424B !important;
   color:#fff !important;
   font-size:11px !important;
   border-color:#132028 !important;
   top:-1px !important;
   position:relative
}
div#userbar input.bbcodes:hover{
   background-color:#697781 !important;
}
div#userbar a[href*="http://www.downtr.net/addnews.html"]{
   margin-right:10px !important;
}
/* header section */
div#header{
   height:80px !important;
   position:relative !important;
   margin-top:-25px;
   z-index:100 !important;
   -moz-border-radius-bottomleft:10px !important;
   -moz-border-radius-bottomright:10px !important;
   background:transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABQAAABQCAIAAACWIsIgAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAHVJREFUeNrsmMENwDAMAp2qM2WGDtD9VyE79B7INf5boGAj4rWft77WVaBQ810l0CyALBdt9mBSS9pQKh9tuSbMpTOl7VsMG7LNw5jOA5Gb2hCZ7Ug1BDlmEKliBr8xg77BdVwOS3zsczPo+SmbeDNAzUeAAQC2kV2Wmr8D4AAAAABJRU5ErkJggg==) left top repeat-x !important;
}
div#header div#headerl{
   width:225px !important;
   height:80px !important;
   -moz-border-radius-bottomleft:10px !important;
   -moz-border-radius-bottomright:10px !important;
   background:transparent url(data:image/png;base64,) left top no-repeat !important;
}
div#header div#headerr{
   height:80px !important;
   background-image:none !important;
}
/* content section */
div#container div#continer2{
   margin:0 10px !important;
}
div#mnbbgr{
   margin-top:-60px;
}
div.blq{
   margin:0 0 15px 0 !important;
   padding-right:15px !important;
   -moz-border-radius:10px !important;
   background-color:#FFF !important;
}
div.blq h2{
   border:1px #2E3C47 solid !important;
   border-left:10px #182325 solid !important;
   border-right:40px #182325 solid !important;
   -moz-border-radius:10px !important;
   background-color:#2E3C47 !important;
   padding:4px 6px 4px 12px !important;
}
div.blq h2 a{
   border-bottom-style:none !important;
   color:#fff !important;
}
div.blq h2 a:hover{
   color:#0097F7 !important;
}
div.blq td.txt div[id*="news-id"]{
   border-style:none !important;
   background-color:transparent !important;
   padding:0 !important;
}
div.blq td.txt>div{
   border:1px #DDD solid !important;
   -moz-border-radius:10px !important;
   background-color:#FBFBFB !important;
   padding:5px !important;
}
/* comments section */
div.blq h2{
   color:#fff !important;
}
td#ctnt div.txt{
   padding:4px !important;
}
div[style*="border: 1px solid rgb(187, 187, 187);"]{
   width:100% !important;
}
textarea#comments{
   width:100% !important;
}
/* navigation styles */
div#mnbbgr{
   background-image:none !important;
}
div#mnbbgr table td#menu{
   width:140px !important;
   background-image:none !important;
}
div#mnbbgr table td#menu div.mpag{
   padding:10px 0 0 5px !important;
   position:relative !important;
   left:-15px !important;
}
div[style*="color: rgb(126, 126, 126); font-size: 11px; position: relative; bottom: 8px;"]{
   margin-left:10px !important;
}
div#mnbbgr table td#menu table#mntt{
   display:none !important;
}
div#mnbbgr table td#menu table.mblk{
    width:100% !important;
}
div#mnbbgr table td#menu input.mainforms[type*="text"]{
   width:125px !important;
   margin-left:10px !important;
   margin-bottom:10px !important;
}
div#mnbbgr table td#menu table.mblk input.bbcodes,
div#mnbbgr table td#menu h2
{
   display:none !important;
}
div#mnbbgr table td#menu div.mpag br{
   display:none !important;
}
div#mnbbgr table td#menu div.mpag a{
   display:block !important;
}
div#mnbbgr table td#menu div.mpag a img{
   display:none !important;
}
div#mnbbgr table td#menu div.mpag a{
   display:block !important;
   height:28px !important;
   width:125px !important;
   margin-bottom:1px !important;
   margin-left:10px !important;
   background-position:-65px top !important;
   background-repeat:no-repeat !important;
   -moz-border-radius:5px !important;
}
div#mnbbgr table td#menu div.mpag a[href*="/program_indir"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="/freeware"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="oyun_indir"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="grafik_indir"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="wallpaper_indir"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="photoshop"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="cep_telefonu"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="videolar"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="blog"]{
   background-image:url(data:image/gif;base64,) !important;
}
div#mnbbgr table td#menu div.mpag a[href*="dosyalar"]{
   background-image:url(data:image/gif;base64,) !important;
}

div#mnbbgr table td#menu div.mpag a:hover{
   background-position:-45px top !important;
}
div#footer{
   height:5px !important;
   border-style:none !important;
}

}
