css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/")
{

.audio_player
{
max-width: 415px !important;
margin-left: 20px !important;
}
  
.album_art_container
{
height: 100% !important;
left: -20px !important;
}

.album_art_container 
{
height: 100% !important;
}

.audio_player_container.has_art 
{
height: 90px !important;
}

.audio_player_container.has_art .album_art_container
{
width: 90px !important;
height: 90px !important;
}

.audio_player_container.has_art.has_visualizer .audio_controls 
{
margin-right: -135px !important;
}

.audio_player .progress_container, .audio_player .seek_overlay, .audio_player .audio_message_container, .audio_player .player_text_wrapper 
{
width: 90% !important;
height: 45px !important;
}

.audio_player_container.has_art .audio_equalizer 
{
padding: 8px 11px !important;
}


.audio_equalizer_band b 
{
width: 100% !important;
margin: 0px 0px 0px -1px !important;
height: 2px !important;
}

.audio_equalizer_band 
{
width: 25px !important;
height: 100% !important;
margin: 0px 5px !important;
}

.audio_visualizer 
{
width: 100% !important;
height: 50% !important;
border: none !important;
}

.peak
{
height: 4px !important;
}

.audio_player_container.has_art .audio_player 
{
padding-right: 169px !important;
}

.audio_player 
{
width: 100% !important;
border-width:1px;	
border-style:dashed;
border-color: rgba(0,0,0,.1);
}

.audio_controls 
{
border: none !important;
}

.audio_player .audio_player_button 
{
height: 45px !important;
top: -17px !important;
}

.audio_preview_container .audio_album_art .album_art_container 
{
position: absolute;
top: 0px;
left: 80px !important;
min-width: 53% !important;
min-height: 53% !important;
max-width: 53% !important;
max-height: 53% !important;
}
}
