css:
@-moz-document domain("duckduckgo.com") { 
html {
    background-color: #ffffff !important;
}
#search_form_input_homepage {
    border-top: 1px solid #c0c0c0 !important;
    border-left: 1px solid #D9D9D9 !important;
    border-right: 1px solid #D9D9D9 !important;
    border-bottom: 1px solid #D9D9D9 !important;
    border-radius: 0 !important;
    background-color: #ffffff !important;
    box-shadow: 0px 0px 0px #ffffff !important;
    height: 20px !important;
    width: 571px !important;
    left: 45px !important;
}
#search_form_input_homepage:focus {
    border: 1px solid #4D90FE !important;
}
#search_wrapper_homepage {
    display: none;
}
#header_content {
    padding-left: 0px !important;
}
#logo_homepage {
    background:url(http://www.google.fr/images/srpr/logo4w.png) no-repeat !important;
    background-size:275px 95px !important;
    height:95px !important;
    width:275px !important;
}
#footer_homepage {
    display: none;
}
#header {
    background-image: none !important;
    background-color: #f1f1f1 !important;
    border-bottom: 1px solid #E5E5E5;
}
#header_logo {
    background-position: -63px 0px !important;
    height: 37px !important;
    width: 95px !important;
    background-size: 294px 45px !important;
    background-image: url('http://ssl.gstatic.com/gb/images/k2_aca6bcc6.png') !important;
    margin-left: 20px !important;
    margin-top: 9px !important;
}
#search_form_input {
    margin-top: 3px !important;
    margin-left: -58px !important;
    border-top: 1px solid #c0c0c0 !important;
    border-left: 1px solid #D9D9D9 !important;
    border-right: 1px solid #D9D9D9 !important;
    border-bottom: 1px solid #D9D9D9 !important;
    border-radius: 0 !important;
    background-color: #ffffff !important;
    box-shadow: 0px 0px 0px #ffffff !important;
    height: 20px !important;
    width: 571px !important;
    left: 220px !important;
}
#search_form_input:focus {
    border: 1px solid #4D90FE !important;
}
#search_form {
    border: 0 !important;
}
#search_wrapper {
    display: none;
}
#search_form_input_clear {
    display: none;
}
#header_button_wrapper {
    display: none;
}
#zero_click_wrapper {
    display: none !important;
}
#ads {
    display: none;
}
.results_links_deep {
    border: 0 !important;
}
.results_links_deep:hover {
    border: 0 !important;
    background-color: #ffffff !important;
}
.highlight_d2 {
    background-color: #ffffff !important;
}
div.results_links_deep a.large {
    color: #1122CC !important;
    font-family: "Helvetica Neue","Segoe UI","Nimbus Sans L","Liberation Sans","Open Sans",FreeSans,Arial,sans-serif !important;
    text-decoration: underline !important;
}
div.results_links_deep a.url {
    color: #009933 !important;
}
div.results_links_deep a.url:hover {
    text-decoration: none !important;
}
div.results_links_deep img {
    display: none !important;
}
#content {
    padding-left: 95px !important;
}
div.results_links_deep a.links_menu {
    display: none !important;
}
div.results_links_deep div.snippet {
    color: #444444 !important;
}
#side_wrapper2 {
    display: none;
}
#ads {
    display: none !important;
}
}
