css:
@-moz-document url-prefix("http://feedly.com"),  url-prefix("http://cloud.feedly.com"){
#mainBar {
  	width:1100 !important;
	}
#feedlyPage {
	margin-left: 0 !important;
	margin-right: 0 !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	width: 99% !important;
	}
#sideArea { 
	display: none !important; 
	}
#floatingBar {
	background-color: #FFF !important;
	}
.entryBody img {
	float: none !important;
	}
h1{
  font-size:22px !important;
}
#floatingBar h1 {
	color:#FC2103 !important;
	font-size:14px !important;
	font-family:微软雅黑,HelveticaNeue-condensed,sans-serif !important;
	font-weight:bold !important;
	width:947px !important;
	}
#feedlyTitleBar{
	color:#FC2103 !important;   
	width:947px !important;
	}
h2 {
	margin-top:15px !important;
	margin-bottom:0px !important;
	}
.u100Frame,.u100frame.selectedEntry {
	padding:10px !important;
	}
 .u100entry {
  margin-left:15px !important;
  max-width:100% !important;
  }
.u100Entry .title  {
	font-size:20px !important;
	color:blue !important;
	font-weight:bold !important;
	}
.u100Entry .title.read {
	font-size:20px !important;
	color:#2F477A !important;
    font-weight:bold !important;
	}

.topWikiWidget{
  display:none !important;
  }
    
#entryContent {
	width:100% !important;
	}
.entryBody {
	max-width:1000px !important;
	}
#Who ,.actions {
	margin-bottom:5px !important;
	top:5px !important;
	width:215px !important;
	}     
element.style {
	width: 1000px !important;
	}
.column_0_2,.column_1_2 {
	width:340px !important;
	padding-right:15px !important;
	}
.column_2_2 {
	width:295px !important;
	}        

.condensed .inlineFrame {
      padding-left:5px !important;
      padding-top:5px !important;
    }


.u4Entry, .u12Entry {
background: #ffffff!important;
border: 1px solid #a79999 !important;
border-radius: 3px!important;
max-width: none!important;
height: 198px!important;
display: block!important;
}

.u4EntryAnnotationHolder {
margin-left: 10px!important;
margin-top: -8px!important;
width: 353.5px!important;
height: 182px!important;
z-index: 10!important;
}

.u4Entry .visual, .u12Entry .visual {
background-color: #ffffff!important;
border: 1px solid #A79999 !important;
border-radius: 3px!important;
width: 353.5px!important;
height: 182px!important;
z-index: 10!important;
}

.u4Entry .marginMaker, .u12Entry div[style="height:auto; margin-left:222px"] {
margin-left: 0!important;
margin-right: 0!important;
max-height: 183px!important;
min-height: 183px!important;
display: block!important;
}

.u4Entry .title.unread, .u12Entry .title.unread {
background: #5897F5 !important;
color: #ffffff !important;
font-weight: bold!important;
margin-top: 15px!important;
padding: 5px 10px 5px 10px!important;
max-height: 54px!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
display: block!important;
z-index: 100!important;
}

.u4Entry .title.read, .u12Entry .title.read {
background: #A79999 !important;
color: #ffffff !important;
font-weight: normal!important;
margin-top: 15px!important;
padding: 5px 10px 5px 10px!important;
max-height: 54px!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
display: block!important;
z-index: 100!important;
}

.u5Entry {
background: #e6eafe!important;
}
.u5EntryList {
width:1100px !important;
}

.u5Entry:hover {
background: #5897f5 !important;
}

.u5Entry .title.unread {
background: #5897F5!important;
color: #ffffff!important;
font-size: 16px!important;
font-weight: bold!important;
line-height: 32px!important;
border-radius: 0!important;
margin: 0!important;
padding: 5px 10px 10px 10px!important;
height: 95px!important;
}

.u5Entry .title.read {
background: #a79999 !important;
color: #ffffff!important;
font-size: 16px!important;
font-weight: normal!important;
line-height: 32px!important;
border-radius: 0!important;
margin: 0!important;
padding: 5px 10px 10px 10px!important;
height: 95px!important;
}

.u5Entry:hover .title.unread,.u4Entry:hover .title.unread {
background: #58e6F5!important;
color:black !important;      
}

.u5Entry:hover .title.read,.u4Entry:hover .title.read {
background: #5897F5!important;
color:black !important;
}

.u5Entry .summary {
display: none!important;
}

.u5Entry .metadata {
color: #000000!important;
max-height: 17px!important;
min-height: 17px!important;
display: block!important;
}

.u5Entry:hover .metadata {
color: #330000!important;
}

.u5Entry div[style="visibility:visible; padding:16px; font-size: 11px"] {
padding-top: 10px!important;
padding-bottom: 5px!important;
}

.u5Entry:hover .nbrRecommendations {
color: #330000!important;
}

.u5Entry:hover .nbrRecommendations.hot {
color: #ff0000!important;
}

.u5Entry div .metadata a:hover {
color: #ffff00!important;
}

.u5Entry span.lessThanAnHour, .u5Entry span.lastModified {
color: #330000!important;
font-weight: normal!important;
}

.u5Entry .action {
color: #e6eafe!important;
}

.u5Entry .visual{
      height:150px !important;
    }
.u5Entry .action:hover {
color: #ffff00!important;
}

.u5Entry .wikiBar.requiresLogin span {
color: #e6eafe!important;
}

.u5Entry .wikiBar.requiresLogin span:hover {
color: #ffff00!important;
}
.u5Entry .visual {
width: 100% !important;
}
.topRecommendedEntry {
background: #ffffff!important;
border: none!important;
box-shadow: 0 0 8px 4px #827c62!important;
margin-bottom: 20px!important;
width: 320px!important;
height: 270px!important;
}

.topRecommendedEntry .visual {
border: none!important;
border-radius: 0!important;
width: 320px!important;
height: 162.5px!important;
position: relative!important;
top: 70px!important;
}

.topRecommendedEntry .title.unread {
background: #5897F5 !important;
color: #ffffff!important;
font-weight: bold!important;
padding: 10px 10px 10px 10px!important;
width: 300px!important;
height: 50px!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
position: relative!important;
top: -166.5px!important;
z-index: 100!important;
}

.topRecommendedEntry .title.read {
background: #5897F5!important;
color: #ffffff!important;
font-weight: normal!important;
padding: 10px 10px 10px 10px!important;
width: 300px!important;
height: 50px!important;
transition: background 0.25s ease 0s,color 0.25s ease 0s!important;
position: relative!important;
top: -166.5px!important;
z-index: 100!important;
}

.topRecommendedEntry a.title:hover {
background: #58e6f5 !important;
color: #000000 !important;
transition-property: background,color!important;
transition-duration: 0.25s,0.25s!important;
transition-timing-function: cubic-bezier(0.25,0.1,0.25,1),cubic-bezier(0.25,0.1,0.25,1)!important;
transition-delay: 0s,0s!important;
-webkit-text-blink: none!important;
-moz-text-blink: none!important;
-ms-text-blink: none!important;
-o-text-blink: none!important;
-webkit-text-decoration-color: #000000!important;
-moz-text-decoration-color: #000000!important;
-ms-text-decoration-color: #000000!important;
-o-text-decoration-color: #000000!important;
-webkit-text-decoration-line: none!important;
-moz-text-decoration-line: none!important;
-ms-text-decoration-line: none!important;
-o-text-decoration-line: none!important;
-webkit-text-decoration-style: solid!important;
-moz-text-decoration-style: solid!important;
-ms-text-decoration-style: solid!important;
-o-text-decoration-style: solid!important;
}

.topRecommendedEntry .summary {
background: rgba(0,0,0,0.8)!important;
color: #ffffff!important;
padding: 0!important;
max-height: 65px!important;
min-height: 65px!important;
-webkit-transition: all 0.8s!important;
-moz-transition: all 0.8s!important;
-ms-transition: all 0.8s!important;
-o-transition: all 0.8s!important;
transition: all 0.8s!important;
position: relative!important;
top: -238.5px!important;
z-index: 5!important;
}

.topRecommendedEntry:hover .summary {
-webkit-transform: translate(0,70px)!important;
-moz-transform: translate(0,70px)!important;
-ms-transform: translate(0,70px)!important;
-o-transform: translate(0,70px)!important;
transform: translate(0,70px)!important;
position: relative!important;
top: -239.5px!important;
z-index: 5!important;
}

.nbrRecommendations {
color: #910e07!important;
font-weight: bold!important;
}

.nbrRecommendations.hot {
color: #ff0000!important;
font-size: 14px!important;
}

.topRecommendedEntry .metadata {
padding: 0 5px 0 5px!important;
max-height: 34px!important;
min-height: 34px!important;
position: relative!important;
top: -69px!important;
}

#recommentationAreaHeadlines span.lessThanAnHour, #recommentationAreaHeadlines span.lastModified {
color: #333333!important;
font-weight: normal!important;
}

.topRecommendedEntry .metadata span[class="wikiBar requiresLogin"] {
margin: 0!important;
padding: 0!important;
width: 150px!important;
position: absolute!important;
left: 5px!important;
bottom: 0!important;
}
.inlineFrame{
    background-position: bottom right !important;
  }
#feedlySignPart {
background-color: #6BA872 !important;
max-width: 300px !important;
margin: 0px;
padding: 20px;
text-align: left !important;
}
div.signAlert {
font-size: 9p !important;
line-height: 20px;
text-align: left;
min-width: 300px !important;
}
.column_0_3,.column_1_3,.column_2_3,.column_3_3{
width: 245px;
}
}
