@-moz-document domain("comicvine.com") {

/*| Comicvine Remix v1.2     |
  | by pikahyper - 2013      |
  | last modified: 7/12/2013 |
  |__________________________|*/
  
/* General - Header */

#nav-main>ul>li:hover, #nav-main>ul>li.active, #nav-main>ul>li.active:hover{background-color:#0098FF!important;}

#nav-main>ul>li:hover:before, #nav-main>ul>li.active:before, #nav-main>ul>li.active:hover:before{background-color:#0098FF!important;}

#nav-main .dropnav dl{
 background:#0098ff;
 background:-moz-linear-gradient(top, #0098ff 0%, #d3f2fc 100%);
 background:linear-gradient(to bottom, #0098ff 0%,#d3f2fc 100%);
 background:-webkit-linear-gradient(top, #0098ff 0%,#d3f2fc 100%);
 background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,#0098ff), color-stop(100%,#d3f2fc));}

#nav-main .dropnav dl dd a:hover{color:#0098FF!important;}

#nav-main .dropnav .reviews a:hover{color:#0098FF!important;}

/* User bar/Search bar */

#user-bar .header-avatar, #user-bar .header-avatar img{border-radius:0!important;}

#user-bar .header-avatar{box-shadow:none!important;}

#user-bar .notification>a{background:url(data:image/png;base64,) 0 center no-repeat!important;}

#user-bar li.dropnav > a:not(.btn){color:#FFF!important;}

#nav-info dd a:not(.btn):hover{color:#0098FF!important;}

#search-filters.search-filters.dropnav-menu > strong{display:none!important;}

#search-filters.search-filters.dropnav-menu > p{display:none!important;}

#nav-main-hold:after{
 background-color:#0098FF!important;
 border-top-color:#0098FF!important;
 border-bottom-color:none!important;}

/* General - Misc */

.feed-hdr dd a:hover{color:#0098FF!important;}

html{overflow-y: scroll;}

.typeahead-full .selected:not(.loading){
 background-image:none!important;
 background-color:#0098FF!important;}

.typeahead-full a.resultWrap,.typeahead-full a.resultWrap:hover{
 background-position:top!important;
 padding:8px 15px 8px 55px!important;
 background-repeat:repeat-x!important;
 background-image:url("http://static.comicvine.com/bundles/phoenixsite/images/core/loose/bg-border-light.png")!important;}

.typeahead-full li:not(.loading):hover{
 background-image:none!important;
 background-color:#0098FF!important;}
 
/* General - Footer */

footer:after{
 box-shadow:none!important;
 background:none!important;
 background-image:none!important;
 border-top:3px solid #0098FF!important;}

footer a:hover, footer .icon-twitter{color:#0098FF!important;}

/* Front page */

.no-touch .kubrick-lead a:hover h2{text-shadow: rgba(0, 152, 255, 0.75) 2px 2px 10px!important;}

.kubrick-info .stats:hover i, .no-touch .spotlight-release a:hover{color:#0098FF!important;}

.spotlight-release dd a.full-text:hover{color:#2c2e30!important;}

.overlay-stats i, .editorial-stats i{background-color:#0098FF!important;}

.overlay-stats i, .editorial-stats i{border:none!important;}

.promo-strip h4{background-color:#0098FF!important;}

.header-border a:hover, .header-borders a:hover{color:#0098FF!important;}

.icon-twitter:before{color:#0098FF!important;}

.promo-twitter-feed .editorial p a{color:#0098FF!important;}

.promo-twitter-feed .editorial p a.twitter-author{color:#696e72!important;}

#forums a.last{color:#696e72!important;}

#forums .table-forums tr:hover td:nth-child(2) div a{color:#0098FF!important;}

/* Links */

.no-touch .editorial>li>a:hover h2, .no-touch .editorial>li>a:hover h3, .no-touch .editorial>li>a:hover:after, .no-touch .editorial>dd>a:hover h2, .no-touch .editorial>dd>a:hover h3, .no-touch .editorial>dd>a:hover:after, .no-touch .editorial>dt>a:hover h2, .no-touch .editorial>dt>a:hover h3, .no-touch .editorial>dt>a:hover:after{color:#0098FF!important;}

a.good{color:#0098FF!important;}

a.neutral:hover{color:#798086;}

a.evil:hover{color:#df2020;}

.aside-pod a:hover{color:#0098FF!important;}

h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover, h6 a:hover{color:#0098FF!important;}

#imageInfo div a{color:#0098FF!important;}

.content-body p a{color:#0098FF!important;}

.content-body ul li a,.content-body ol li a{color:#0098FF!important;}

/* Buttons */

.btn{
 color:#FFF!important;
 border-color:#3EA2FF!important;
 background-color:#0098FF!important;}

.btn:hover{
 color:#FFF!important;
 transition:none!important;
 -webkit-transition:none!important;
 background-image:linear-gradient(rgba(85,196,232,1),rgba(0,152,255,1));
 background-image:-webkit-linear-gradient(rgba(85,196,232,1),rgba(0,152,255,1));}

.btn-primary{
 border-color:#3EA2FF!important;
 background-color:#0098FF!important;}
  
.btn-primary:hover{background-color:#3EA2FF!important;}

.aside-pod a.btn:hover{color:#FFF!important;}

/* Wiki */

.tooltip-inner{background-color:#0098FF!important;}

.tooltip-arrow{border-top-color:#0098FF!important;}

.tooltip.in{opacity:1!important;}

.wiki-hdr p.wiki-descriptor a{color:#0098FF!important;}

.wiki_object.wiki-volume.skin-no .span5{
 width:404px!important;
 margin-left:20px!important;}

body.wiki_object .last-edit a{color: #2c2e30!important;}

body.wiki_object .last-edit a:hover{color:#0098FF!important;}
        
.wiki_object.wiki-volume.skin-no .span7{width:63.4%!important;}

.wiki_object.skin-no .span7 .js-toc-content .display-view a{color:#0098FF!important;}

.dropnav .sub-nav-toc-menu li a:hover{color:#0098FF!important;}

#wiki-relatedList a{color:#0098FF!important;}

.wiki_object.wiki-volume.skin-no .span7 > h2:hover a:first-child{color:#0098FF!important;}

.wiki-details h3{margin-bottom:0!important;}

.wiki-details td a, .wiki-details th a{color:#0098FF!important;}

.wiki-details .wiki-relation a{color:#0098FF!important;}

#credit-content section .wiki-details li.wiki-relation-search{width:100%!important;}

.wiki_object .btn.js-expand,.wiki_object #volume-content .header-border .btn.pull-right{
